        }

        // Security Section
        yPos = Math.max(yPos + 10, 240); // Ensure it's near bottom
        pdf.setFillColor(...colors.danger);
        pdf.rect(15, yPos, 180, 12, 'F');
        pdf.setFontSize(12);
        pdf.setTextColor(...colors.white);
        pdf.setFont('helvetica', 'bold');
        pdf.text('ðŸ”’ SECURITY & COMPLIANCE', 20, yPos + 7);

        yPos += 15;
        pdf.setFillColor(255, 245, 245); // Light red background
        pdf.setDrawColor(...colors.danger);
        pdf.rect(15, yPos, 180, 20, 'FD');

        pdf.setFontSize(9);
        pdf.setTextColor(...colors.dark);
        pdf.setFont('helvetica', 'normal');
        pdf.text('âœ“ HIPAA Compliant  âœ“ Blockchain Verified  âœ“ End-to-End Encrypted', 20, yPos + 7);
        pdf.text('âœ“ Digital Signature  âœ“ Audit Trail  âœ“ Tamper-Proof Hash Verification', 20, yPos + 14);

        // Footer with gradient
        yPos = 275;
        pdf.setFillColor(...colors.dark);
        pdf.rect(0, yPos, 210, 22, 'F');

