                                                <button
                                                    onClick={() => exportToPDF(record)}
                                                    className="text-blue-600 hover:text-blue-900 mr-3"
                                                    title="Download PDF"
                                                >
                                                    ðŸ“„ PDF
                                                </button>
                                                <button
                                                    onClick={() => navigator.clipboard.writeText(record.recordHash)}
                                                    className="text-green-600 hover:text-green-900"
                                                    title="Copy Hash"
