import React, { useState, useEffect } from 'react';
import { WalletConnection, Transaction } from '../../types';

interface EnhancedWalletConnectProps {
    className?: string;
}

const EnhancedWalletConnect: React.FC<EnhancedWalletConnectProps> = ({ className = '' }) => {
    const [walletConnection, setWalletConnection] = useState<WalletConnection | null>(null);
    const [isConnecting, setIsConnecting] = useState(false);
    const [transactions, setTransactions] = useState<Transaction[]>([]);
    const [showQR, setShowQR] = useState(false);

    // Simulate blockchain transactions for demo
    const mockTransactions: Transaction[] = [
        {
            id: '1',
            hash: '0xabc123...def456',
            type: 'record_upload',
            timestamp: new Date(Date.now() - 3600000).toISOString(),
            status: 'confirmed',
            gasUsed: '21000',
            gasPrice: '20',
            blockNumber: 18745623
        },
        {
            id: '2',
            hash: '0x789xyz...321abc',
            type: 'access_grant',
            timestamp: new Date(Date.now() - 7200000).toISOString(),
            status: 'confirmed',
            gasUsed: '45000',
            gasPrice: '22',
            blockNumber: 18745589
        },
        {
            id: '3',
            hash: '0xdef789...456ghi',
            type: 'prescription_mint',
            timestamp: new Date(Date.now() - 300000).toISOString(),
            status: 'pending',
            gasUsed: '65000',
            gasPrice: '25'
        }
    ];

    useEffect(() => {
        // Check if wallet is already connected
        checkExistingConnection();
        setTransactions(mockTransactions);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const checkExistingConnection = async () => {
        if (typeof window.ethereum !== 'undefined') {
            try {
                const accounts = await window.ethereum.request({ method: 'eth_accounts' });
                if (accounts.length > 0) {
                    const chainId = await window.ethereum.request({ method: 'eth_chainId' });
                    const balance = await window.ethereum.request({
                        method: 'eth_getBalance',
                        params: [accounts[0], 'latest']
                    });

                    setWalletConnection({
                        address: accounts[0],
                        provider: 'metamask',
                        chainId: parseInt(chainId, 16),
                        isConnected: true,
                        balance: (parseInt(balance, 16) / 1e18).toFixed(4)
                    });
                }
            } catch (error) {
                console.error('Error checking wallet connection:', error);
            }
        }
    };

    const connectMetaMask = async () => {
        if (typeof window.ethereum === 'undefined') {
            alert('MetaMask is not installed. Please install MetaMask to continue.');
            return;
        }

        setIsConnecting(true);
        try {
            const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
            const chainId = await window.ethereum.request({ method: 'eth_chainId' });
            const balance = await window.ethereum.request({
                method: 'eth_getBalance',
                params: [accounts[0], 'latest']
            });

            setWalletConnection({
                address: accounts[0],
                provider: 'metamask',
                chainId: parseInt(chainId, 16),
                isConnected: true,
                balance: (parseInt(balance, 16) / 1e18).toFixed(4)
            });

        } catch (error) {
            console.error('Error connecting to MetaMask:', error);
            alert('Failed to connect to MetaMask');
        } finally {
            setIsConnecting(false);
        }
    };

    const connectWalletConnect = async () => {
        setIsConnecting(true);
        try {
            // Simulate WalletConnect connection process
            await new Promise(resolve => setTimeout(resolve, 2000));
            
            // Mock successful connection
            setWalletConnection({
                address: '0x742d35Cc6551C0532a0fD0f7a7e2f1B987B5e9c4',
                provider: 'walletconnect',
                chainId: 1,
                isConnected: true,
                balance: '2.5431'
            });
            
            alert('âœ… WalletConnect connected successfully!\n\nNote: This is a demo connection. In production, this would:\nâ€¢ Generate a QR code for mobile wallets\nâ€¢ Support 100+ mobile wallets\nâ€¢ Enable cross-device signing');
        } catch (error) {
            alert('âŒ WalletConnect connection failed');
        } finally {
            setIsConnecting(false);
        }
    };

    const connectCoinbase = async () => {
        setIsConnecting(true);
        try {
            // Simulate Coinbase Wallet connection
            await new Promise(resolve => setTimeout(resolve, 1500));
            
            // Mock successful connection
            setWalletConnection({
                address: '0x8Ba1f109551bD432803012645Hac136c22ABB49d',
                provider: 'coinbase',
                chainId: 1,
                isConnected: true,
                balance: '1.8732'
            });
            
            alert('âœ… Coinbase Wallet connected successfully!\n\nNote: This is a demo connection. In production, this would:\nâ€¢ Use Coinbase Wallet SDK\nâ€¢ Support direct exchange integration\nâ€¢ Enable fiat on/off ramps');
        } catch (error) {
            alert('âŒ Coinbase Wallet connection failed');
        } finally {
            setIsConnecting(false);
        }
    };

    const disconnectWallet = () => {
        setWalletConnection(null);
    };

    const switchNetwork = async (chainId: number) => {
        if (typeof window.ethereum !== 'undefined') {
            try {
                await window.ethereum.request({
                    method: 'wallet_switchEthereumChain',
                    params: [{ chainId: `0x${chainId.toString(16)}` }],
                });
            } catch (error) {
                console.error('Error switching network:', error);
            }
        }
    };

    const getNetworkName = (chainId: number): string => {
        switch (chainId) {
            case 1: return 'Ethereum Mainnet';
            case 5: return 'Goerli Testnet';
            case 137: return 'Polygon Mainnet';
            case 80001: return 'Mumbai Testnet';
            default: return `Chain ID: ${chainId}`;
        }
    };

    const getTransactionIcon = (type: Transaction['type']): string => {
        switch (type) {
            case 'record_upload': return 'ðŸ“„';
            case 'access_grant': return 'ðŸ”';
            case 'prescription_mint': return 'ðŸ’Š';
            case 'consent_update': return 'âœï¸';
            default: return 'âš¡';
        }
    };

    const getStatusColor = (status: Transaction['status']): string => {
        switch (status) {
            case 'confirmed': return 'text-green-600 bg-green-100';
            case 'pending': return 'text-yellow-600 bg-yellow-100';
            case 'failed': return 'text-red-600 bg-red-100';
            default: return 'text-gray-600 bg-gray-100';
        }
    };

    const shortenAddress = (address: string): string => {
        return `${address.slice(0, 6)}...${address.slice(-4)}`;
    };

    const copyToClipboard = async (text: string) => {
        try {
            await navigator.clipboard.writeText(text);
            alert('Copied to clipboard!');
        } catch (error) {
            console.error('Failed to copy:', error);
        }
    };

    return (
        <div className={`space-y-6 p-6 ${className}`}>
            {/* Header */}
            <div className="bg-white rounded-lg shadow p-6">
                <h2 className="text-2xl font-bold text-gray-900 mb-2">ðŸ”— Blockchain Wallet Connection</h2>
                <p className="text-gray-600">
                    Connect your crypto wallet to access blockchain features and verify your identity on the decentralized network.
                </p>
            </div>

            {/* Connection Status */}
            {walletConnection ? (
                <div className="bg-white rounded-lg shadow p-6">
                    <div className="flex items-center justify-between mb-4">
                        <div className="flex items-center">
                            <div className="text-green-600 text-2xl mr-3">âœ…</div>
                            <div>
                                <h3 className="text-lg font-medium text-gray-900">Wallet Connected</h3>
                                <p className="text-sm text-gray-600">Successfully connected via {walletConnection.provider}</p>
                            </div>
                        </div>
                        <button
                            onClick={disconnectWallet}
                            className="px-4 py-2 bg-red-600 text-white text-sm rounded-lg hover:bg-red-700"
                        >
                            Disconnect
                        </button>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="space-y-3">
                            <div>
                                <label className="text-sm font-medium text-gray-700">Wallet Address</label>
                                <div className="flex items-center mt-1">
                                    <code className="text-sm bg-gray-100 px-2 py-1 rounded flex-1">
                                        {shortenAddress(walletConnection.address)}
                                    </code>
                                    <button
                                        onClick={() => copyToClipboard(walletConnection.address)}
                                        className="ml-2 p-1 text-gray-600 hover:text-gray-800"
                                        title="Copy full address"
                                    >
                                        ðŸ“‹
                                    </button>
                                </div>
                            </div>

                            <div>
                                <label className="text-sm font-medium text-gray-700">Balance</label>
                                <p className="text-lg font-bold text-gray-900">{walletConnection.balance} ETH</p>
                            </div>
                        </div>

                        <div className="space-y-3">
                            <div>
                                <label className="text-sm font-medium text-gray-700">Network</label>
                                <div className="flex items-center mt-1">
                                    <span className="text-sm bg-blue-100 text-blue-800 px-2 py-1 rounded">
                                        {getNetworkName(walletConnection.chainId)}
                                    </span>
                                </div>
                            </div>

                            <div className="flex space-x-2">
                                <button
                                    onClick={() => switchNetwork(1)}
                                    className="px-3 py-1 text-xs bg-gray-200 text-gray-700 rounded hover:bg-gray-300"
                                >
                                    Mainnet
                                </button>
                                <button
                                    onClick={() => switchNetwork(137)}
                                    className="px-3 py-1 text-xs bg-purple-200 text-purple-700 rounded hover:bg-purple-300"
                                >
                                    Polygon
                                </button>
                                <button
                                    onClick={() => switchNetwork(5)}
                                    className="px-3 py-1 text-xs bg-yellow-200 text-yellow-700 rounded hover:bg-yellow-300"
                                >
                                    Testnet
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="bg-white rounded-lg shadow p-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-4">Connect Your Wallet</h3>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        {/* MetaMask */}
                        <button
                            onClick={connectMetaMask}
                            disabled={isConnecting}
                            className="flex flex-col items-center p-6 border-2 border-gray-200 rounded-lg hover:border-orange-500 hover:bg-orange-50 transition-colors"
                        >
                            <div className="text-4xl mb-2">ðŸ¦Š</div>
                            <h4 className="font-medium text-gray-900">MetaMask</h4>
                            <p className="text-sm text-gray-600 text-center mt-1">
                                Most popular browser wallet
                            </p>
                            {isConnecting && <div className="animate-spin text-orange-500 mt-2">â³</div>}
                        </button>

                        {/* WalletConnect */}
                        <button
                            onClick={connectWalletConnect}
                            className="flex flex-col items-center p-6 border-2 border-gray-200 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-colors"
                        >
                            <div className="text-4xl mb-2">ðŸ“±</div>
                            <h4 className="font-medium text-gray-900">WalletConnect</h4>
                            <p className="text-sm text-gray-600 text-center mt-1">
                                Connect with mobile wallets
                            </p>
                        </button>

                        {/* Coinbase Wallet */}
                        <button
                            onClick={connectCoinbase}
                            className="flex flex-col items-center p-6 border-2 border-gray-200 rounded-lg hover:border-blue-600 hover:bg-blue-50 transition-colors"
                        >
                            <div className="text-4xl mb-2">ðŸ’™</div>
                            <h4 className="font-medium text-gray-900">Coinbase</h4>
                            <p className="text-sm text-gray-600 text-center mt-1">
                                Coinbase Wallet integration
                            </p>
                        </button>
                    </div>

                    <div className="mt-6 p-4 bg-blue-50 rounded-lg">
                        <div className="flex items-start">
                            <div className="text-blue-600 text-lg mr-2">â„¹ï¸</div>
                            <div className="text-sm text-blue-700">
                                <strong>Why connect a wallet?</strong>
                                <ul className="mt-2 space-y-1">
                                    <li>â€¢ Verify your identity on the blockchain</li>
                                    <li>â€¢ Sign transactions for medical records</li>
                                    <li>â€¢ Access decentralized healthcare features</li>
                                    <li>â€¢ Ensure data integrity and ownership</li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Blockchain Actions */}
            {walletConnection && (
                <div className="bg-white rounded-lg shadow p-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-4">ðŸš€ Blockchain Actions</h3>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                        <button className="flex flex-col items-center p-4 bg-green-50 border border-green-200 rounded-lg hover:bg-green-100 transition-colors">
                            <div className="text-2xl mb-2">ðŸ“„</div>
                            <span className="text-sm font-medium text-green-700">Store Record on Chain</span>
                        </button>

                        <button className="flex flex-col items-center p-4 bg-blue-50 border border-blue-200 rounded-lg hover:bg-blue-100 transition-colors">
                            <div className="text-2xl mb-2">ðŸŽ–ï¸</div>
