        } catch (error) {
            console.error('PDF download error:', error);
            alert(`‚ùå Failed to download PDF: ${error instanceof Error ? error.message : 'Unknown error'}`);
        }
    };

    const handleDownloadPatientPDF = async (patientId: string) => {
        const patient = myPatients.find(p => p.id === patientId);
        if (!patient) {
            alert('Patient not found');
            return;
        }

        const patientRecords = recentRecords.filter(record => record.patientId === patientId);

        if (patientRecords.length === 0) {
            alert(`No medical records found for ${patient.name}. Create some records first.`);
            return;
        }

        try {
            const wantEncryption = window.confirm(`üîê PDF Download Options for ${patient.name}

Total Records: ${patientRecords.length}

Do you want to:
‚Ä¢ Click "OK" to download an ENCRYPTED PDF (password protected)
‚Ä¢ Click "Cancel" to download a regular PDF

Note: The PDF will include a large "CONFIDENTIAL" watermark at the top.`);

            let password = '';
            if (wantEncryption) {
                password = prompt('üîí Enter password for PDF encryption:') || '';
                if (!password.trim()) {
                    alert('‚ùå Password is required for encryption');
                    return;
                }
            }

            if (patientRecords.length === 1) {
                // Single record
                await pdfService.downloadMedicalRecordPDF(patientRecords[0], patient, {
                    includeWatermark: true,
                    isEncrypted: wantEncryption,
                    password: password
                });
            } else {
                // Multiple records - use batch PDF
                const batchPDF = await pdfService.generateBatchPDF(patientRecords, [patient], {
                    includeWatermark: true,
                    isEncrypted: wantEncryption,
                    password: password
                });

                // Create download link for batch PDF
                const url = URL.createObjectURL(batchPDF);
                const link = document.createElement('a');
                link.href = url;

                const encryptionSuffix = wantEncryption ? '_encrypted' : '';
                const filename = `medical_records_${patient.name.replace(/\s+/g, '_')}_batch${encryptionSuffix}.pdf`;
                link.download = filename;

                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
                URL.revokeObjectURL(url);
            }

            const encryptionStatus = wantEncryption ? 'ENCRYPTED' : 'UNENCRYPTED';
            alert(`‚úÖ PDF Downloaded Successfully!

üë§ Patient: ${patient.name}
üìÑ Records: ${patientRecords.length} medical record(s)
üîê Status: ${encryptionStatus}
üö® Watermark: CONFIDENTIAL (at top of document)

${wantEncryption ? '‚ö†Ô∏è Remember your password - you\'ll need it to view the encrypted content!' : ''}

The PDF has been saved to your downloads folder.`);
        } catch (error) {
            console.error('PDF download error:', error);
            alert(`‚ùå Failed to download PDF: ${error instanceof Error ? error.message : 'Unknown error'}`);
        }
    };

