import React, { useState, useRef, useEffect, useCallback } from 'react';
import Modal from '../UI/Modal';
import Badge from '../UI/Badge';
import CameraTroubleshootingGuide from './CameraTroubleshootingGuide';

interface EnhancedBiometricVerificationProps {
    isOpen: boolean;
    onClose: () => void;
    onSuccess: () => void;
    onFailure?: (error: string) => void;
    isLoading?: boolean;
}

const EnhancedBiometricVerification: React.FC<EnhancedBiometricVerificationProps> = ({
    isOpen,
    onClose,
    onSuccess,
    onFailure,
    isLoading = false
}) => {
    const [verificationStep, setVerificationStep] = useState<'init' | 'camera' | 'processing' | 'success' | 'error'>('init');
    const [cameraStream, setCameraStream] = useState<MediaStream | null>(null);
    const [faceDetected, setFaceDetected] = useState(false);
    const [countdown, setCountdown] = useState(5);
    const [errorMessage, setErrorMessage] = useState('');
    const [showTroubleshooting, setShowTroubleshooting] = useState(false);
    const videoRef = useRef<HTMLVideoElement>(null);
    const canvasRef = useRef<HTMLCanvasElement>(null);

    const handleCaptureFace = useCallback(async () => {
        if (!videoRef.current || !canvasRef.current) return;

        setVerificationStep('processing');

        const canvas = canvasRef.current;
        const video = videoRef.current;
        const context = canvas.getContext('2d');

        if (context) {
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;
            context.drawImage(video, 0, 0);

            // Simulate biometric processing
            setTimeout(() => {
                // Stop camera stream
                if (cameraStream) {
                    cameraStream.getTracks().forEach(track => track.stop());
                    setCameraStream(null);
                }

                // Simulate successful face recognition
                const success = Math.random() > 0.1; // 90% success rate for demo

                if (success) {
                    setVerificationStep('success');
                    setTimeout(() => {
                        onSuccess();
                    }, 1500);
                } else {
                    setErrorMessage('Face recognition failed. Please try again.');
                    setVerificationStep('error');
                    onFailure?.('Face recognition failed');
                }
            }, 3000);
        }
    }, [cameraStream, onSuccess, onFailure]);

    useEffect(() => {
        if (verificationStep === 'camera' && countdown > 0) {
            const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
            return () => clearTimeout(timer);
        } else if (verificationStep === 'camera' && countdown === 0) {
            handleCaptureFace();
        }
    }, [verificationStep, countdown, handleCaptureFace]);

    useEffect(() => {
        return () => {
            if (cameraStream) {
                cameraStream.getTracks().forEach(track => track.stop());
            }
        };
    }, [cameraStream]);

    const checkCameraPermissions = async (): Promise<{ hasPermission: boolean; error?: string }> => {
        try {
            // Check if we're in a secure context (HTTPS or localhost)
            if (!window.isSecureContext && window.location.hostname !== 'localhost') {
                return { 
                    hasPermission: false, 
                    error: 'Camera access requires HTTPS or localhost. Please ensure you are using a secure connection.' 
                };
            }

            // Check if mediaDevices is supported
            if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
                return { 
                    hasPermission: false, 
                    error: 'Your browser does not support camera access. Please use Chrome, Firefox, or Safari.' 
                };
            }

            // Try to get camera permissions without actually starting the camera
            try {
                const permissionStatus = await navigator.permissions.query({ name: 'camera' as PermissionName });
                if (permissionStatus.state === 'denied') {
                    return { 
                        hasPermission: false, 
                        error: 'Camera permission is permanently denied. Please check your browser settings.' 
                    };
                }
            } catch (permissionError) {
                // Permissions API might not be supported, we'll try getUserMedia directly
                console.log('Permissions API not available, will attempt direct camera access');
            }

            return { hasPermission: true };
        } catch (error: any) {
            return { 
                hasPermission: false, 
                error: `Permission check failed: ${error.message}` 
            };
        }
    };

    const startCamera = async () => {
        try {
            setVerificationStep('camera');
            setCountdown(5);
            setErrorMessage('');

            // First check permissions
            const permissionCheck = await checkCameraPermissions();
            if (!permissionCheck.hasPermission) {
                throw new Error(permissionCheck.error);
            }

            // Start with more permissive camera constraints
            let stream: MediaStream;
            
            try {
                // Try with ideal constraints first
                stream = await navigator.mediaDevices.getUserMedia({
                    video: {
                        width: { ideal: 640, min: 320 },
                        height: { ideal: 480, min: 240 },
                        facingMode: 'user'
                    },
                    audio: false
                });
            } catch (constraintError) {
                console.log('Ideal constraints failed, trying basic constraints:', constraintError);
                try {
                    // Fallback to basic constraints
                    stream = await navigator.mediaDevices.getUserMedia({
                        video: true,
                        audio: false
                    });
                } catch (basicError) {
                    console.log('Basic constraints also failed, trying minimal constraints:', basicError);
                    // Last resort: minimal constraints
                    stream = await navigator.mediaDevices.getUserMedia({
                        video: { facingMode: 'user' },
                        audio: false
                    });
                }
            }

            setCameraStream(stream);

            if (videoRef.current) {
                videoRef.current.srcObject = stream;
                
                // Handle video load events
                videoRef.current.onloadedmetadata = () => {
                    if (videoRef.current) {
                        videoRef.current.play().catch(playError => {
                            console.error('Video play failed:', playError);
                            setErrorMessage('Failed to start video playback. Your browser might be blocking autoplay.');
                            setVerificationStep('error');
                        });
                    }
                };

                videoRef.current.onerror = (videoError) => {
                    console.error('Video error:', videoError);
                    setErrorMessage('Video stream error occurred. Please check your camera connection.');
                    setVerificationStep('error');
                };
            }

            // Simulate face detection after a delay
            setTimeout(() => {
                setFaceDetected(true);
            }, 2000);

        } catch (error: any) {
            console.error('Camera access error:', error);

            let errorMsg = 'Camera access failed';
            let errorDetails = '';
            let instructions = '';

            // Handle specific error types with detailed instructions
            if (error.name === 'NotAllowedError' || error.name === 'PermissionDeniedError') {
                errorMsg = 'ðŸš« Camera Permission Denied';
                errorDetails = 'Camera access was blocked by your browser.';
                instructions = getPermissionInstructions();
            } else if (error.name === 'NotFoundError' || error.name === 'DevicesNotFoundError') {
                errorMsg = 'ðŸ“· No Camera Found';
                errorDetails = 'No camera device was detected on this device.';
                instructions = 'Please ensure your camera is connected and working properly.';
            } else if (error.name === 'NotReadableError' || error.name === 'TrackStartError') {
                errorMsg = 'âš ï¸ Camera In Use';
                errorDetails = 'Camera is being used by another application.';
                instructions = 'Please close other applications that might be using the camera (Zoom, Skype, etc.) and try again.';
            } else if (error.name === 'OverconstrainedError' || error.name === 'ConstraintNotSatisfiedError') {
                errorMsg = 'âš™ï¸ Camera Constraints Error';
                errorDetails = 'Camera does not support the required specifications.';
                instructions = 'Your camera might not support the required resolution. Please try with a different camera.';
            } else if (error.message?.includes('secure context') || error.message?.includes('HTTPS')) {
                errorMsg = 'ðŸ”’ Secure Connection Required';
                errorDetails = 'Camera access requires HTTPS or localhost.';
                instructions = 'Please access this page via HTTPS (https://) or localhost for camera functionality.';
            } else if (error.message?.includes('not supported')) {
                errorMsg = 'ðŸŒ Browser Not Supported';
                errorDetails = 'Your browser does not support camera access.';
                instructions = 'Please use a modern browser like Chrome, Firefox, or Safari.';
            } else {
                errorMsg = 'âŒ Camera Error';
                errorDetails = error.message || 'Unknown camera error occurred.';
                instructions = 'Please try refreshing the page or using a different browser.';
            }

            const fullErrorMessage = `${errorMsg}\n\n${errorDetails}\n\n${instructions}`;
            setErrorMessage(fullErrorMessage);
            setVerificationStep('error');
            onFailure?.(`Camera access failed: ${error.name || error.message || 'Unknown error'}`);
        }
    };

    const getPermissionInstructions = (): string => {
        const userAgent = navigator.userAgent.toLowerCase();
        
        if (userAgent.includes('chrome')) {
            return 'ðŸ“‹ Chrome Instructions:\n1. Click the camera icon in the address bar\n2. Select "Always allow" for camera access\n3. Refresh the page and try again';
        } else if (userAgent.includes('firefox')) {
            return 'ðŸ“‹ Firefox Instructions:\n1. Click the shield icon in the address bar\n2. Click "Allow" for camera permissions\n3. Refresh the page and try again';
        } else if (userAgent.includes('safari')) {
            return 'ðŸ“‹ Safari Instructions:\n1. Go to Safari > Preferences > Websites\n2. Select "Camera" from the left panel\n3. Set this website to "Allow"\n4. Refresh the page and try again';
        } else {
            return 'ðŸ“‹ General Instructions:\n1. Check your browser settings for camera permissions\n2. Allow camera access for this website\n3. Refresh the page and try again';
        }
    };

    const handleRetry = () => {
        setVerificationStep('init');
        setFaceDetected(false);
        setCountdown(5);
        setErrorMessage('');
        if (cameraStream) {
            cameraStream.getTracks().forEach(track => track.stop());
            setCameraStream(null);
        }
    };

    const renderContent = () => {
        switch (verificationStep) {
            case 'init':
                return (
                    <div className="text-center space-y-6">
                        <div className="mx-auto h-20 w-20 flex items-center justify-center rounded-full bg-gradient-to-r from-blue-100 to-purple-100 mb-4">
                            <svg className="h-10 w-10 text-indigo-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                            </svg>
                        </div>
                        <div>
                            <h3 className="text-xl font-semibold text-gray-900 mb-2">
                                ðŸ” Biometric Face Verification
                            </h3>
                            <p className="text-sm text-gray-600 mb-4">
                                Secure your healthcare data with advanced facial recognition technology
                            </p>
                            
                            {/* Camera Permission Guide */}
                            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-4">
                                <h4 className="text-sm font-semibold text-blue-900 mb-2">ðŸ“¸ Camera Permission Required</h4>
                                <div className="text-xs text-blue-800 space-y-1 text-left">
                                    <p>â€¢ Your browser will ask for camera permission</p>
                                    <p>â€¢ Click "Allow" to enable face verification</p>
                                    <p>â€¢ Ensure good lighting for best results</p>
                                    <p>â€¢ Position your face clearly in view</p>
                                </div>
                            </div>

                            {/* System Requirements */}
                            <div className="bg-gray-50 border border-gray-200 rounded-lg p-4 mb-4">
                                <h4 className="text-sm font-semibold text-gray-900 mb-2">âš™ï¸ System Requirements</h4>
                                <div className="text-xs text-gray-700 space-y-1 text-left">
                                    <p>âœ… Working camera/webcam</p>
                                    <p>âœ… Modern browser (Chrome, Firefox, Safari)</p>
                                    <p>âœ… HTTPS connection (secure)</p>
                                    <p>âœ… Camera not in use by other apps</p>
                                </div>
                            </div>
                            
                            <div className="flex justify-center space-x-2 mb-6">
                                <Badge variant="success" size="sm">ðŸ›¡ï¸ Secure</Badge>
                                <Badge variant="primary" size="sm">âš¡ Fast</Badge>
                                <Badge variant="secondary" size="sm">ðŸ”’ Private</Badge>
                            </div>
                        </div>
                        <div className="space-y-3">
                            <button
                                onClick={startCamera}
                                disabled={isLoading}
                                className="w-full bg-indigo-600 text-white py-3 px-4 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:opacity-50 font-medium"
                            >
                                {isLoading ? 'ðŸ”„ Initializing Camera...' : 'ðŸ“· Start Face Verification'}
                            </button>
                            <button
                                onClick={() => {
                                    console.log('âš ï¸ User chose to skip biometric verification');
                                    onSuccess(); // Proceed directly to 2FA
                                }}
                                className="w-full border border-gray-300 text-gray-700 py-2 px-4 rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 text-sm"
                            >
                                â­ï¸ Skip Camera & Use 2FA Only
                            </button>
                        </div>
                    </div>
                );

            case 'camera':
                return (
                    <div className="text-center space-y-4">
                        <div className="relative">
                            <video
                                ref={videoRef}
                                className="w-full max-w-md mx-auto rounded-lg border-4 border-dashed border-indigo-300"
                                autoPlay
                                muted
                                playsInline
                            />
                            <canvas ref={canvasRef} className="hidden" />

                            {/* Face detection overlay */}
                            <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                                <div className={`w-48 h-48 rounded-full border-4 ${faceDetected ? 'border-green-500' : 'border-yellow-500'} ${faceDetected ? 'animate-pulse' : ''}`}>
                                    <div className="w-full h-full flex items-center justify-center">
                                        {faceDetected && (
                                            <svg className="w-8 h-8 text-green-500" fill="currentColor" viewBox="0 0 20 20">
                                                <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                                            </svg>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="space-y-2">
                            <div className="flex justify-center space-x-2">
                                <Badge variant={faceDetected ? "success" : "secondary"} size="sm">
                                    {faceDetected ? "âœ“ Face Detected" : "ðŸ” Detecting Face..."}
                                </Badge>
                            </div>

                            <div className="text-lg font-mono font-bold text-indigo-600">
                                {countdown > 0 ? `Capturing in ${countdown}...` : 'Processing...'}
                            </div>

                            <p className="text-sm text-gray-600">
                                Please look directly at the camera and remain still
                            </p>
                        </div>
                    </div>
                );

            case 'processing':
                return (
                    <div className="text-center space-y-6">
                        <div className="mx-auto h-20 w-20 flex items-center justify-center rounded-full bg-gradient-to-r from-yellow-100 to-orange-100">
                            <div className="animate-spin rounded-full h-12 w-12 border-b-4 border-yellow-600"></div>
                        </div>
                        <div>
                            <h3 className="text-xl font-semibold text-gray-900 mb-2">
                                ðŸ”„ Processing Biometric Data
                            </h3>
                            <p className="text-sm text-gray-600">
                                Analyzing facial features and matching against secure database...
                            </p>
                        </div>
                        <div className="bg-blue-50 p-4 rounded-lg">
                            <p className="text-sm text-blue-700">
                                ðŸ”’ Your biometric data is processed locally and never stored permanently
                            </p>
                        </div>
                    </div>
                );

            case 'success':
                return (
                    <div className="text-center space-y-6">
                        <div className="mx-auto h-20 w-20 flex items-center justify-center rounded-full bg-gradient-to-r from-green-100 to-emerald-100">
                            <svg className="h-12 w-12 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                        </div>
                        <div>
                            <h3 className="text-xl font-semibold text-green-900 mb-2">
                                âœ… Biometric Verification Successful
                            </h3>
                            <p className="text-sm text-gray-600">
                                Face recognition completed successfully. Proceeding to two-factor authentication...
                            </p>
                        </div>
                        <Badge variant="success" size="lg">Verified âœ“</Badge>
                    </div>
                );

            case 'error':
