import React, { useState, useRef } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import CryptoJS from 'crypto-js';
import { MedicalRecord } from '../../types';

interface PatientHealthRecordsProps {
    className?: string;
}

const PatientHealthRecords: React.FC<PatientHealthRecordsProps> = ({ className = '' }) => {
    const [records, setRecords] = useState<MedicalRecord[]>([
        {
            id: 'MR001',
            patientId: 'PT001',
            doctorId: 'DR001',
            title: 'Annual Physical Examination',
            description: 'Routine annual physical examination with all vital signs normal.',
            date: '2024-01-15',
            diagnosis: 'Healthy - no issues found',
            prescription: 'Continue regular exercise and balanced diet',
            isEncrypted: true,
            blockchainHash: '0x8e7f9c2d1a5b3e6f9c8a4d2e7f1b5c9a3e6d8f2b7c5a9e3f6d1c8b4a7e2f5d9',
            ipfsHash: 'QmYjtig7VJQ6XsnUjqqJvj7QaMcCAwtrgNdahSiFofrE7o',
            isVerified: true,
            accessPermissions: ['DR001', 'PT001']
        }
    ]);

    const [showForm, setShowForm] = useState(false);
    const [selectedRecord, setSelectedRecord] = useState<MedicalRecord | null>(null);
    const [formData, setFormData] = useState({
        patientId: '',
        doctorId: '',
        title: '',
        description: '',
        diagnosis: '',
        prescription: '',
        date: new Date().toISOString().split('T')[0]
    });

    const recordRef = useRef<HTMLDivElement>(null);

    // Generate unique hash for record
    const generateRecordHash = (record: Omit<MedicalRecord, 'id' | 'blockchainHash' | 'ipfsHash' | 'isVerified'>): string => {
        const recordString = JSON.stringify({
            ...record,
            timestamp: Date.now()
        });
        return CryptoJS.SHA256(recordString).toString();
    };

    // Add new record
    const handleAddRecord = () => {
        const recordData = {
            ...formData,
            isEncrypted: true,
            accessPermissions: [formData.doctorId, formData.patientId]
        };

        const newRecord: MedicalRecord = {
            id: `MR${String(records.length + 1).padStart(3, '0')}`,
            ...recordData,
            blockchainHash: generateRecordHash(recordData),
            ipfsHash: `Qm${CryptoJS.SHA256(JSON.stringify(recordData)).toString().substring(0, 44)}`,
            isVerified: true
        };

        setRecords([...records, newRecord]);
        setFormData({
            patientId: '',
            doctorId: '',
            title: '',
            description: '',
            diagnosis: '',
            prescription: '',
            date: new Date().toISOString().split('T')[0]
        });
        setShowForm(false);
    };

    // Download PDF
    const downloadPDF = async (record: MedicalRecord) => {
        // Create a temporary div for PDF content
        const tempDiv = document.createElement('div');
        tempDiv.style.position = 'absolute';
        tempDiv.style.left = '-9999px';
        tempDiv.style.width = '800px';
        tempDiv.style.padding = '40px';
        tempDiv.style.backgroundColor = 'white';
        tempDiv.style.fontFamily = 'Arial, sans-serif';
        
        tempDiv.innerHTML = `
            <div style="text-align: center; margin-bottom: 30px; border-bottom: 2px solid #3b82f6; padding-bottom: 20px;">
                <h1 style="color: #1e40af; margin: 0; font-size: 24px;">üè• EMRChains Healthcare</h1>
                <p style="color: #6b7280; margin: 5px 0 0 0;">Medical Record Report</p>
            </div>
            
            <div style="margin-bottom: 30px;">
                <h2 style="color: #374151; border-bottom: 1px solid #e5e7eb; padding-bottom: 10px;">üìã Record Information</h2>
                <table style="width: 100%; border-collapse: collapse; margin-top: 15px;">
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold; width: 150px;">Record ID:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb;">${record.id}</td>
                    </tr>
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold;">Patient ID:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb;">${record.patientId}</td>
                    </tr>
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold;">Doctor ID:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb;">${record.doctorId}</td>
                    </tr>
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold;">Date:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb;">${new Date(record.date).toLocaleDateString()}</td>
                    </tr>
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold;">Title:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb;">${record.title}</td>
                    </tr>
                </table>
            </div>

            <div style="margin-bottom: 30px;">
                <h2 style="color: #374151; border-bottom: 1px solid #e5e7eb; padding-bottom: 10px;">üìù Medical Details</h2>
                <div style="margin-top: 15px;">
                    <h3 style="color: #4b5563; margin-bottom: 8px;">Description:</h3>
                    <p style="background: #f9fafb; padding: 15px; border: 1px solid #e5e7eb; border-radius: 6px; margin-bottom: 15px;">${record.description}</p>
                    
                    <h3 style="color: #4b5563; margin-bottom: 8px;">Diagnosis:</h3>
                    <p style="background: #fef3c7; padding: 15px; border: 1px solid #fbbf24; border-radius: 6px; margin-bottom: 15px;">${record.diagnosis}</p>
                    
                    <h3 style="color: #4b5563; margin-bottom: 8px;">Prescription:</h3>
                    <p style="background: #dcfce7; padding: 15px; border: 1px solid #22c55e; border-radius: 6px;">${record.prescription}</p>
                </div>
            </div>

            <div style="margin-bottom: 30px;">
                <h2 style="color: #374151; border-bottom: 1px solid #e5e7eb; padding-bottom: 10px;">üîê Security & Verification</h2>
                <table style="width: 100%; border-collapse: collapse; margin-top: 15px;">
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold; width: 150px;">Blockchain Hash:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; font-family: monospace; font-size: 12px; word-break: break-all;">${record.blockchainHash}</td>
                    </tr>
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold;">IPFS Hash:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; font-family: monospace; font-size: 12px;">${record.ipfsHash}</td>
                    </tr>
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold;">Encrypted:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb;">${record.isEncrypted ? '‚úÖ Yes' : '‚ùå No'}</td>
                    </tr>
                    <tr>
                        <td style="padding: 8px; border: 1px solid #e5e7eb; background: #f9fafb; font-weight: bold;">Verified:</td>
                        <td style="padding: 8px; border: 1px solid #e5e7eb;">${record.isVerified ? '‚úÖ Verified' : '‚è≥ Pending'}</td>
                    </tr>
                </table>
            </div>

            <div style="margin-top: 40px; padding-top: 20px; border-top: 1px solid #e5e7eb; text-align: center; color: #6b7280; font-size: 12px;">
                <p>This document was generated by EMRChains Healthcare System</p>
                <p>Generated on: ${new Date().toLocaleString()}</p>
                <p style="margin-top: 10px; font-weight: bold;">‚ö†Ô∏è This document contains confidential medical information</p>
            </div>
        `;

        document.body.appendChild(tempDiv);

        try {
            const canvas = await html2canvas(tempDiv, {
                scale: 2,
                useCORS: true,
                allowTaint: true,
                backgroundColor: '#ffffff'
            });

            const imgData = canvas.toDataURL('image/png');
            const pdf = new jsPDF('p', 'mm', 'a4');
            
            const pdfWidth = pdf.internal.pageSize.getWidth();
            const pdfHeight = pdf.internal.pageSize.getHeight();
            const imgWidth = canvas.width;
            const imgHeight = canvas.height;
            const ratio = Math.min(pdfWidth / imgWidth, pdfHeight / imgHeight);
            const imgX = (pdfWidth - imgWidth * ratio) / 2;
            const imgY = 10;

            pdf.addImage(imgData, 'PNG', imgX, imgY, imgWidth * ratio, imgHeight * ratio);
            pdf.save(`medical-record-${record.id}-${record.patientId}.pdf`);
        } catch (error) {
            console.error('Error generating PDF:', error);
            alert('Error generating PDF. Please try again.');
        } finally {
            document.body.removeChild(tempDiv);
        }
    };

    return (
        <div className={`space-y-6 ${className}`}>
            {/* Header */}
            <div className="bg-white rounded-lg shadow-lg p-6">
                <div className="flex justify-between items-center">
                    <div>
                        <h2 className="text-2xl font-bold text-gray-900 mb-2">üè• Patient Health Records</h2>
                        <p className="text-gray-600">Secure, encrypted, and blockchain-verified medical records</p>
                    </div>
                    <button
                        onClick={() => setShowForm(true)}
                        className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                    >
                        ‚ûï Add New Record
                    </button>
                </div>
            </div>

            {/* Add Record Form */}
            {showForm && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-2xl max-h-[90vh] overflow-y-auto">
                        <div className="flex justify-between items-center mb-6">
                            <h3 className="text-xl font-bold text-gray-900">üìù Add New Medical Record</h3>
                            <button
                                onClick={() => setShowForm(false)}
                                className="text-gray-500 hover:text-gray-700 text-xl"
                            >
                                ‚úï
                            </button>
                        </div>

                        <div className="space-y-4">
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">
                                        Patient ID
                                    </label>
                                    <input
                                        type="text"
                                        value={formData.patientId}
                                        onChange={(e) => setFormData({ ...formData, patientId: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        placeholder="Enter patient ID"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">
                                        Doctor ID
                                    </label>
                                    <input
                                        type="text"
                                        value={formData.doctorId}
                                        onChange={(e) => setFormData({ ...formData, doctorId: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        placeholder="Enter doctor ID"
                                        required
                                    />
                                </div>
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Record Title
                                </label>
                                <input
                                    type="text"
                                    value={formData.title}
                                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    placeholder="e.g., Annual Physical Examination"
                                    required
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Date
                                </label>
                                <input
                                    type="date"
                                    value={formData.date}
                                    onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    required
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Description
                                </label>
                                <textarea
                                    value={formData.description}
                                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    rows={3}
                                    placeholder="Detailed description of the medical examination or procedure"
                                    required
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Diagnosis
                                </label>
                                <textarea
                                    value={formData.diagnosis}
                                    onChange={(e) => setFormData({ ...formData, diagnosis: e.target.value })}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    rows={2}
                                    placeholder="Medical diagnosis and findings"
                                    required
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Prescription/Treatment
                                </label>
                                <textarea
                                    value={formData.prescription}
                                    onChange={(e) => setFormData({ ...formData, prescription: e.target.value })}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    rows={2}
                                    placeholder="Prescribed medications, treatments, or recommendations"
                                    required
                                />
                            </div>
                        </div>

                        <div className="flex justify-end space-x-3 mt-6 pt-6 border-t">
                            <button
                                onClick={() => setShowForm(false)}
                                className="px-4 py-2 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={handleAddRecord}
                                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                                disabled={!formData.patientId || !formData.doctorId || !formData.title || !formData.description}
                            >
                                üîê Add Secure Record
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Records List */}
            <div className="space-y-4">
                {records.map((record) => (
                    <div key={record.id} className="bg-white rounded-lg shadow-lg p-6">
                        <div className="flex justify-between items-start mb-4">
                            <div className="flex-1">
                                <div className="flex items-center space-x-3 mb-2">
                                    <h3 className="text-lg font-semibold text-gray-900">{record.title}</h3>
                                    {record.isVerified && (
                                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                                            ‚úÖ Verified
                                        </span>
                                    )}
                                    {record.isEncrypted && (
                                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                            üîê Encrypted
                                        </span>
                                    )}
                                </div>
                                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                                    <div>
                                        <span className="text-gray-500">Record ID:</span>
                                        <p className="font-medium">{record.id}</p>
                                    </div>
                                    <div>
                                        <span className="text-gray-500">Patient ID:</span>
                                        <p className="font-medium">{record.patientId}</p>
                                    </div>
                                    <div>
                                        <span className="text-gray-500">Doctor ID:</span>
                                        <p className="font-medium">{record.doctorId}</p>
                                    </div>
                                    <div>
                                        <span className="text-gray-500">Date:</span>
                                        <p className="font-medium">{new Date(record.date).toLocaleDateString()}</p>
                                    </div>
                                </div>
                            </div>

                            <div className="flex space-x-2">
                                <button
                                    onClick={() => setSelectedRecord(record)}
                                    className="px-3 py-1 text-sm bg-blue-100 text-blue-700 rounded-lg hover:bg-blue-200 transition-colors"
                                >
                                    üëÅÔ∏è View
                                </button>
                                <button
                                    onClick={() => downloadPDF(record)}
                                    className="px-3 py-1 text-sm bg-green-100 text-green-700 rounded-lg hover:bg-green-200 transition-colors"
                                >
                                    üìÑ PDF
                                </button>
                            </div>
                        </div>

                        <div className="space-y-3">
                            <div>
                                <span className="text-sm text-gray-500">Description:</span>
                                <p className="text-gray-700">{record.description}</p>
                            </div>
                            <div>
                                <span className="text-sm text-gray-500">Diagnosis:</span>
                                <p className="text-gray-700">{record.diagnosis}</p>
                            </div>
                            <div>
                                <span className="text-sm text-gray-500">Prescription:</span>
                                <p className="text-gray-700">{record.prescription}</p>
                            </div>
                        </div>

                        {/* Blockchain Hash */}
                        <div className="mt-4 pt-4 border-t">
                            <div className="flex items-center space-x-2 mb-2">
                                <span className="text-sm font-medium text-gray-700">üîó Blockchain Hash:</span>
                                <span className="text-xs text-green-600">‚óè Verified on blockchain</span>
                            </div>
                            <div className="bg-gray-50 rounded-lg p-3">
                                <code className="text-xs text-gray-600 break-all font-mono">
                                    {record.blockchainHash}
                                </code>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Record Detail Modal */}
            {selectedRecord && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
                        <div className="flex justify-between items-center mb-6">
                            <h3 className="text-xl font-bold text-gray-900">üìã Medical Record Details</h3>
                            <button
                                onClick={() => setSelectedRecord(null)}
                                className="text-gray-500 hover:text-gray-700 text-xl"
                            >
                                ‚úï
                            </button>
                        </div>

                        <div ref={recordRef} className="space-y-6">
                            {/* Basic Information */}
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <div className="bg-blue-50 rounded-lg p-4">
                                    <h4 className="font-medium text-blue-900 mb-3">üìã Record Information</h4>
                                    <div className="space-y-2 text-sm">
                                        <div><span className="text-blue-700">ID:</span> <strong>{selectedRecord.id}</strong></div>
                                        <div><span className="text-blue-700">Patient:</span> <strong>{selectedRecord.patientId}</strong></div>
                                        <div><span className="text-blue-700">Doctor:</span> <strong>{selectedRecord.doctorId}</strong></div>
                                        <div><span className="text-blue-700">Date:</span> <strong>{new Date(selectedRecord.date).toLocaleDateString()}</strong></div>
                                    </div>
                                </div>

                                <div className="bg-green-50 rounded-lg p-4">
                                    <h4 className="font-medium text-green-900 mb-3">üîê Security Status</h4>
                                    <div className="space-y-2 text-sm">
                                        <div className="flex items-center">
                                            <span className="text-green-600 mr-2">‚úÖ</span>
                                            <span>Blockchain Verified</span>
                                        </div>
                                        <div className="flex items-center">
                                            <span className="text-green-600 mr-2">üîê</span>
                                            <span>End-to-End Encrypted</span>
                                        </div>
                                        <div className="flex items-center">
                                            <span className="text-green-600 mr-2">üè•</span>
                                            <span>HIPAA Compliant</span>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Medical Details */}
                            <div className="space-y-4">
                                <div>
                                    <h4 className="font-medium text-gray-900 mb-2">üìù Title</h4>
                                    <p className="bg-gray-50 rounded-lg p-3">{selectedRecord.title}</p>
                                </div>

                                <div>
                                    <h4 className="font-medium text-gray-900 mb-2">üìÑ Description</h4>
                                    <p className="bg-gray-50 rounded-lg p-3">{selectedRecord.description}</p>
                                </div>

                                <div>
                                    <h4 className="font-medium text-gray-900 mb-2">ü©∫ Diagnosis</h4>
                                    <p className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">{selectedRecord.diagnosis}</p>
                                </div>

                                <div>
                                    <h4 className="font-medium text-gray-900 mb-2">üíä Prescription/Treatment</h4>
                                    <p className="bg-green-50 border border-green-200 rounded-lg p-3">{selectedRecord.prescription}</p>
                                </div>
                            </div>

                            {/* Blockchain Information */}
                            <div className="bg-gray-50 rounded-lg p-4">
                                <h4 className="font-medium text-gray-900 mb-3">‚õìÔ∏è Blockchain & Security Hashes</h4>
                                <div className="space-y-3">
                                    <div>
                                        <span className="text-sm text-gray-600">Blockchain Hash:</span>
                                        <code className="block mt-1 text-xs bg-white rounded p-2 border break-all font-mono">
                                            {selectedRecord.blockchainHash}
                                        </code>
                                    </div>
                                    <div>
                                        <span className="text-sm text-gray-600">IPFS Hash:</span>
                                        <code className="block mt-1 text-xs bg-white rounded p-2 border break-all font-mono">
                                            {selectedRecord.ipfsHash}
                                        </code>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="flex justify-end space-x-3 mt-6 pt-6 border-t">
                            <button
                                onClick={() => downloadPDF(selectedRecord)}
                                className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                            >
                                üìÑ Download PDF
                            </button>
                            <button
                                onClick={() => setSelectedRecord(null)}
                                className="px-4 py-2 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
                            >
                                Close
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {records.length === 0 && (
                <div className="bg-white rounded-lg shadow-lg p-12 text-center">
                    <div className="text-6xl mb-4">üìã</div>
                    <h3 className="text-xl font-medium text-gray-900 mb-2">No Medical Records</h3>
                    <p className="text-gray-600 mb-6">Start by adding your first patient health record.</p>
                    <button
                        onClick={() => setShowForm(true)}
                        className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                    >
                        ‚ûï Add First Record
                    </button>
                </div>
            )}
        </div>
    );
};

export default PatientHealthRecords;
