import React, { useState, useEffect } from 'react';
import SecurityDashboard from '../Security/SecurityDashboard';
import FileEncryptor from '../Security/FileEncryptor';
import EnhancedWalletConnect from '../Blockchain/EnhancedWalletConnect';
import EnhancedBiometricLogin from '../Auth/EnhancedBiometricLogin';
import Enhanced2FA from '../Auth/Enhanced2FA';
import PatientHealthRecords from './PatientHealthRecords';
import { SecurityEvent } from '../../types';

interface HealthcareSecurityDashboardProps {
    className?: string;
}

const HealthcareSecurityDashboard: React.FC<HealthcareSecurityDashboardProps> = ({
    className = ''
}) => {
    const [activeTab, setActiveTab] = useState<'dashboard' | 'encryption' | 'wallet' | 'auth' | 'records'>('dashboard');
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [authStep, setAuthStep] = useState<'biometric' | '2fa' | 'complete'>('biometric');
    const [securityAlerts] = useState<SecurityEvent[]>([]);
    const [userProfile] = useState({
        name: 'Dr. Sarah Johnson',
        email: 'sarah.johnson@emrchains.health',
        phone: '+1-555-0123',
        role: 'Healthcare Provider',
        walletAddress: '0x742d35Cc6551C0532a0fD0f7a7e2f1B987B5e9c4'
    });

    useEffect(() => {
        // Check if user is already authenticated
        const isAuth = localStorage.getItem('emr_authenticated') === 'true';
        if (isAuth) {
            setIsAuthenticated(true);
            setAuthStep('complete');
        }
    }, []);

    const handleBiometricSuccess = () => {
        setAuthStep('2fa');
    };

    const handle2FASuccess = () => {
        setIsAuthenticated(true);
        setAuthStep('complete');
        localStorage.setItem('emr_authenticated', 'true');
    };

    const handleAuthError = (error: string) => {
        console.error('Authentication error:', error);
        // In real app, show proper error handling
        alert(error);
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
        setAuthStep('biometric');
        localStorage.removeItem('emr_authenticated');
    };

    const tabConfig = [
        { id: 'dashboard', label: '🛡️ Security Overview', icon: '🏠' },
        { id: 'records', label: '🏥 Health Records', icon: '📋' },
        { id: 'encryption', label: '🔐 File Security', icon: '📁' },
        { id: 'wallet', label: '⛓️ Blockchain', icon: '🔗' },
        { id: 'auth', label: '🔒 Authentication', icon: '👤' }
    ] as const;

    // Authentication screens
    if (!isAuthenticated) {
        return (
            <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6">
                <div className="max-w-md mx-auto">
                    {/* Logo/Header */}
                    <div className="text-center mb-8">
                        <div className="text-6xl mb-4">🏥</div>
                        <h1 className="text-3xl font-bold text-gray-900">EMRChains</h1>
                        <p className="text-gray-600 mt-2">Secure Healthcare Management System</p>
                        <div className="mt-4 flex items-center justify-center space-x-2">
                            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                            <span className="text-sm text-green-600 font-medium">HIPAA Compliant</span>
                        </div>
                    </div>

                    {/* Authentication Steps */}
                    <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
                        <div className="flex justify-center space-x-4 mb-6">
                            <div className={`flex items-center ${authStep === 'biometric' ? 'text-blue-600' : authStep === '2fa' || authStep === 'complete' ? 'text-green-600' : 'text-gray-400'}`}>
                                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${authStep === 'biometric' ? 'bg-blue-100' : authStep === '2fa' || authStep === 'complete' ? 'bg-green-100' : 'bg-gray-100'}`}>
                                    {authStep === '2fa' || authStep === 'complete' ? '✓' : '1'}
                                </div>
                                <span className="ml-2 text-sm">Biometric</span>
                            </div>

                            <div className={`w-8 border-t-2 mt-4 ${authStep === '2fa' || authStep === 'complete' ? 'border-green-300' : 'border-gray-200'}`}></div>

                            <div className={`flex items-center ${authStep === '2fa' ? 'text-blue-600' : authStep === 'complete' ? 'text-green-600' : 'text-gray-400'}`}>
                                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${authStep === '2fa' ? 'bg-blue-100' : authStep === 'complete' ? 'bg-green-100' : 'bg-gray-100'}`}>
                                    {authStep === 'complete' ? '✓' : '2'}
                                </div>
                                <span className="ml-2 text-sm">2FA</span>
                            </div>
                        </div>

                        {authStep === 'biometric' && (
                            <EnhancedBiometricLogin
                                onSuccess={handleBiometricSuccess}
                                onError={handleAuthError}
                            />
                        )}

                        {authStep === '2fa' && (
                            <Enhanced2FA
                                onSuccess={handle2FASuccess}
                                onError={handleAuthError}
                                onResendCode={() => console.log('Resending code...')}
                                userEmail={userProfile.email}
                                phoneNumber={userProfile.phone}
                            />
                        )}
                    </div>

                    {/* Security Features */}
                    <div className="bg-white rounded-lg shadow-lg p-4">
                        <h3 className="font-medium text-gray-900 mb-3">🔒 Security Features</h3>
                        <div className="space-y-2 text-sm text-gray-600">
                            <div className="flex items-center">
                                <span className="text-green-500 mr-2">✓</span>
                                End-to-end encryption
                            </div>
                            <div className="flex items-center">
                                <span className="text-green-500 mr-2">✓</span>
                                Blockchain-verified records
                            </div>
                            <div className="flex items-center">
                                <span className="text-green-500 mr-2">✓</span>
                                Multi-factor authentication
                            </div>
                            <div className="flex items-center">
                                <span className="text-green-500 mr-2">✓</span>
                                HIPAA compliance
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    // Main dashboard (authenticated)
    return (
        <div className={`min-h-screen bg-gray-50 ${className}`}>
            {/* Header */}
            <header className="bg-white shadow-sm border-b">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center h-16">
                        <div className="flex items-center">
                            <div className="text-2xl mr-3">🏥</div>
                            <div>
                                <h1 className="text-xl font-bold text-gray-900">EMRChains</h1>
                                <p className="text-xs text-gray-500">Healthcare Security Platform</p>
                            </div>
                        </div>

                        <div className="flex items-center space-x-4">
                            <div className="flex items-center space-x-2">
                                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                                <span className="text-sm text-green-600">Secure Session</span>
                            </div>

                            <div className="text-right">
                                <p className="text-sm font-medium text-gray-900">{userProfile.name}</p>
                                <p className="text-xs text-gray-500">{userProfile.role}</p>
                            </div>

                            <button
                                onClick={handleLogout}
                                className="px-3 py-1 text-sm bg-red-100 text-red-700 rounded-lg hover:bg-red-200"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </header>

            {/* Navigation Tabs */}
            <nav className="bg-white border-b">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex space-x-8">
                        {tabConfig.map((tab) => (
                            <button
                                key={tab.id}
                                onClick={() => setActiveTab(tab.id)}
                                className={`py-4 px-1 border-b-2 font-medium text-sm ${activeTab === tab.id
                                    ? 'border-blue-500 text-blue-600'
                                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                                    }`}
                            >
                                <span className="mr-2">{tab.icon}</span>
                                {tab.label}
                            </button>
                        ))}
                    </div>
                </div>
            </nav>

            {/* Main Content */}
            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                {activeTab === 'dashboard' && <SecurityDashboard />}
                {activeTab === 'records' && <PatientHealthRecords />}
                {activeTab === 'encryption' && <FileEncryptor />}
                {activeTab === 'wallet' && <EnhancedWalletConnect />}
                {activeTab === 'auth' && (
                    <div className="space-y-6">
                        <div className="bg-white rounded-lg shadow p-6">
                            <h2 className="text-2xl font-bold text-gray-900 mb-4">🔐 Authentication Settings</h2>
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <div className="space-y-4">
                                    <h3 className="text-lg font-medium text-gray-900">Current Session</h3>
                                    <div className="space-y-2">
                                        <div className="flex items-center justify-between p-3 bg-green-50 rounded-lg">
                                            <span className="text-sm text-green-700">Biometric Authentication</span>
                                            <span className="text-green-600">✅ Verified</span>
                                        </div>
                                        <div className="flex items-center justify-between p-3 bg-green-50 rounded-lg">
                                            <span className="text-sm text-green-700">Two-Factor Authentication</span>
                                            <span className="text-green-600">✅ Verified</span>
                                        </div>
                                        <div className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
                                            <span className="text-sm text-blue-700">Session Encryption</span>
                                            <span className="text-blue-600">🔒 Active</span>
                                        </div>
                                    </div>
                                </div>

                                <div className="space-y-4">
                                    <h3 className="text-lg font-medium text-gray-900">Quick Actions</h3>
                                    <div className="space-y-2">
                                        <button className="w-full text-left p-3 bg-gray-50 rounded-lg hover:bg-gray-100">
                                            <span className="text-sm text-gray-700">🔄 Reset 2FA Settings</span>
                                        </button>
                                        <button className="w-full text-left p-3 bg-gray-50 rounded-lg hover:bg-gray-100">
                                            <span className="text-sm text-gray-700">📱 Add New Device</span>
                                        </button>
                                        <button className="w-full text-left p-3 bg-gray-50 rounded-lg hover:bg-gray-100">
                                            <span className="text-sm text-gray-700">📋 View Security Logs</span>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </main>

            {/* Security Alerts Notification */}
            {securityAlerts.length > 0 && (
                <div className="fixed bottom-4 right-4 bg-red-600 text-white p-4 rounded-lg shadow-lg">
                    <div className="flex items-center">
                        <span className="text-lg mr-2">🚨</span>
                        <div>
                            <p className="font-medium">Security Alert</p>
                            <p className="text-sm">{securityAlerts.length} new security events</p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default HealthcareSecurityDashboard;
