import React, { useState, useEffect } from 'react';
import { PatientRecord } from '../../types';
import jsPDF from 'jspdf';
import * as CryptoJS from 'crypto-js';

interface PatientHealthRecordsProps {
    className?: string;
}

const PatientHealthRecords: React.FC<PatientHealthRecordsProps> = ({ className = '' }) => {
    const [records, setRecords] = useState<PatientRecord[]>([]);
    const [showAddForm, setShowAddForm] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState<'createdAt' | 'patientName' | 'doctorName'>('createdAt');
    const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');

    // Form state
    const [formData, setFormData] = useState({
        patientName: '',
        doctorName: '',
        dateOfBirth: '',
        diagnosis: '',
        prescription: '',
        notes: ''
    });

    useEffect(() => {
        loadRecords();
    }, []);

    const loadRecords = () => {
        const savedRecords = localStorage.getItem('patientHealthRecords');
        if (savedRecords) {
            setRecords(JSON.parse(savedRecords));
        }
    };

    const saveRecords = (newRecords: PatientRecord[]) => {
        localStorage.setItem('patientHealthRecords', JSON.stringify(newRecords));
        setRecords(newRecords);
    };

    const generateNextId = (prefix: string, existingRecords: PatientRecord[]): string => {
        let maxNumber = 0;

        if (prefix === 'MR') {
            existingRecords.forEach(record => {
                const match = record.recordId.match(/^MR(\d+)$/);
                if (match) {
                    maxNumber = Math.max(maxNumber, parseInt(match[1]));
                }
            });
        } else if (prefix === 'PT') {
            const patientIds = existingRecords.map(r => r.patientId);
            const uniquePatientIds = Array.from(new Set(patientIds));
            uniquePatientIds.forEach(id => {
                const match = id.match(/^PT(\d+)$/);
                if (match) {
                    maxNumber = Math.max(maxNumber, parseInt(match[1]));
                }
            });
        } else if (prefix === 'DR') {
            const doctorIds = existingRecords.map(r => r.doctorId);
            const uniqueDoctorIds = Array.from(new Set(doctorIds));
            uniqueDoctorIds.forEach(id => {
                const match = id.match(/^DR(\d+)$/);
                if (match) {
                    maxNumber = Math.max(maxNumber, parseInt(match[1]));
                }
            });
        }

        return `${prefix}${String(maxNumber + 1).padStart(3, '0')}`;
    };

    const generateRecordHash = (record: Omit<PatientRecord, 'id' | 'recordHash' | 'createdAt' | 'updatedAt'>): string => {
        const dataString = JSON.stringify({
            recordId: record.recordId,
            patientId: record.patientId,
            doctorId: record.doctorId,
            patientName: record.patientName,
            doctorName: record.doctorName,
            dateOfBirth: record.dateOfBirth,
            diagnosis: record.diagnosis,
            prescription: record.prescription,
            notes: record.notes
        });
        return CryptoJS.SHA256(dataString).toString();
    };

    const getExistingPatientId = (patientName: string): string | null => {
        const existingRecord = records.find(r =>
            r.patientName.toLowerCase() === patientName.toLowerCase()
        );
        return existingRecord ? existingRecord.patientId : null;
    };

    const getExistingDoctorId = (doctorName: string): string | null => {
        const existingRecord = records.find(r =>
            r.doctorName.toLowerCase() === doctorName.toLowerCase()
        );
        return existingRecord ? existingRecord.doctorId : null;
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();

        if (!formData.patientName || !formData.doctorName || !formData.diagnosis) {
            alert('Please fill in all required fields (Patient Name, Doctor Name, and Diagnosis)');
            return;
        }

        const now = new Date().toISOString();
        const recordId = generateNextId('MR', records);
        const patientId = getExistingPatientId(formData.patientName) || generateNextId('PT', records);
        const doctorId = getExistingDoctorId(formData.doctorName) || generateNextId('DR', records);

        const newRecordData = {
            recordId,
            patientId,
            doctorId,
            patientName: formData.patientName,
            doctorName: formData.doctorName,
            dateOfBirth: formData.dateOfBirth,
            diagnosis: formData.diagnosis,
            prescription: formData.prescription,
            notes: formData.notes,
            isActive: true
        };

        const newRecord: PatientRecord = {
            id: `${recordId}_${Date.now()}`,
            ...newRecordData,
            recordHash: generateRecordHash(newRecordData),
            createdAt: now,
            updatedAt: now
        };

        const updatedRecords = [...records, newRecord];
        saveRecords(updatedRecords);

        // Reset form
        setFormData({
            patientName: '',
            doctorName: '',
            dateOfBirth: '',
            diagnosis: '',
            prescription: '',
            notes: ''
        });
        setShowAddForm(false);
    };

    const exportToPDF = async (record: PatientRecord) => {
        const pdf = new jsPDF();

        // Header
        pdf.setFontSize(20);
        pdf.setFont('helvetica', 'bold');
        pdf.text('MEDICAL RECORD', 105, 20, { align: 'center' });

        // Medical record details
        pdf.setFontSize(12);
        pdf.setFont('helvetica', 'normal');

        let yPos = 40;
        const lineHeight = 8;

        // Record information
        pdf.setFont('helvetica', 'bold');
        pdf.text('RECORD INFORMATION', 20, yPos);
        yPos += lineHeight * 2;

        pdf.setFont('helvetica', 'normal');
        pdf.text(`Record ID: ${record.recordId}`, 20, yPos);
        yPos += lineHeight;
        pdf.text(`Record Hash: ${record.recordHash}`, 20, yPos);
        yPos += lineHeight;
        pdf.text(`Created: ${new Date(record.createdAt).toLocaleDateString()}`, 20, yPos);
        yPos += lineHeight * 2;

        // Patient information
        pdf.setFont('helvetica', 'bold');
        pdf.text('PATIENT INFORMATION', 20, yPos);
        yPos += lineHeight * 2;

        pdf.setFont('helvetica', 'normal');
        pdf.text(`Patient ID: ${record.patientId}`, 20, yPos);
        yPos += lineHeight;
        pdf.text(`Patient Name: ${record.patientName}`, 20, yPos);
        yPos += lineHeight;
        pdf.text(`Date of Birth: ${record.dateOfBirth || 'Not provided'}`, 20, yPos);
        yPos += lineHeight * 2;

        // Doctor information
        pdf.setFont('helvetica', 'bold');
        pdf.text('DOCTOR INFORMATION', 20, yPos);
        yPos += lineHeight * 2;

        pdf.setFont('helvetica', 'normal');
        pdf.text(`Doctor ID: ${record.doctorId}`, 20, yPos);
        yPos += lineHeight;
        pdf.text(`Doctor Name: ${record.doctorName}`, 20, yPos);
        yPos += lineHeight * 2;

        // Medical details
        pdf.setFont('helvetica', 'bold');
        pdf.text('MEDICAL DETAILS', 20, yPos);
        yPos += lineHeight * 2;

        pdf.setFont('helvetica', 'normal');
        pdf.text('Diagnosis:', 20, yPos);
        yPos += lineHeight;
        const diagnosisLines = pdf.splitTextToSize(record.diagnosis, 170);
        pdf.text(diagnosisLines, 20, yPos);
        yPos += diagnosisLines.length * lineHeight + lineHeight;

        if (record.prescription) {
            pdf.text('Prescription:', 20, yPos);
            yPos += lineHeight;
            const prescriptionLines = pdf.splitTextToSize(record.prescription, 170);
            pdf.text(prescriptionLines, 20, yPos);
            yPos += prescriptionLines.length * lineHeight + lineHeight;
        }

        if (record.notes) {
            pdf.text('Notes:', 20, yPos);
            yPos += lineHeight;
            const notesLines = pdf.splitTextToSize(record.notes, 170);
            pdf.text(notesLines, 20, yPos);
        }

        // Footer
        pdf.setFontSize(10);
        pdf.setFont('helvetica', 'italic');
        pdf.text('This document was generated by SecureHealth System', 105, 280, { align: 'center' });
        pdf.text(`Generated on: ${new Date().toLocaleString()}`, 105, 285, { align: 'center' });

        // Save PDF
        pdf.save(`Medical_Record_${record.recordId}_${record.patientName.replace(/\s+/g, '_')}.pdf`);
    };

    const filteredRecords = records
        .filter(record =>
            record.patientName.toLowerCase().includes(searchTerm.toLowerCase()) ||
            record.doctorName.toLowerCase().includes(searchTerm.toLowerCase()) ||
            record.recordId.toLowerCase().includes(searchTerm.toLowerCase()) ||
            record.diagnosis.toLowerCase().includes(searchTerm.toLowerCase())
        )
        .sort((a, b) => {
            let aValue: string | number = a[sortBy];
            let bValue: string | number = b[sortBy];

            if (sortBy === 'createdAt') {
                aValue = new Date(a.createdAt).getTime();
                bValue = new Date(b.createdAt).getTime();
            }

            if (sortOrder === 'asc') {
                return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
            } else {
                return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
            }
        });

    return (
        <div className={`space-y-6 p-6 ${className}`}>
            {/* Header */}
            <div className="bg-white rounded-lg shadow p-6">
                <div className="flex items-center justify-between">
                    <div>
                        <h2 className="text-2xl font-bold text-gray-900 mb-2">🏥 Patient Health Records</h2>
                        <p className="text-gray-600">
                            Manage patient medical records with auto-generated IDs and secure hash verification.
                        </p>
                    </div>
                    <button
                        onClick={() => setShowAddForm(true)}
                        className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center"
                    >
                        ➕ Add New Record
                    </button>
                </div>
            </div>

            {/* Search and Sort */}
            <div className="bg-white rounded-lg shadow p-6">
                <div className="flex flex-col sm:flex-row gap-4">
                    <div className="flex-1">
                        <input
                            type="text"
                            placeholder="Search records by patient name, doctor, record ID, or diagnosis..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        />
                    </div>
                    <div className="flex gap-2">
                        <select
                            value={sortBy}
                            onChange={(e) => setSortBy(e.target.value as any)}
                            className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        >
                            <option value="createdAt">Sort by Date</option>
                            <option value="patientName">Sort by Patient</option>
                            <option value="doctorName">Sort by Doctor</option>
                        </select>
                        <button
                            onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                            className="px-3 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
                        >
                            {sortOrder === 'asc' ? '↑' : '↓'}
                        </button>
                    </div>
                </div>
            </div>

            {/* Statistics */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center">
                        <div className="text-2xl mr-3">📋</div>
                        <div>
                            <p className="text-sm text-gray-600">Total Records</p>
                            <p className="text-xl font-bold text-gray-900">{records.length}</p>
                        </div>
                    </div>
                </div>
                <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center">
                        <div className="text-2xl mr-3">👥</div>
                        <div>
                            <p className="text-sm text-gray-600">Unique Patients</p>
                            <p className="text-xl font-bold text-gray-900">
                                {new Set(records.map(r => r.patientId)).size}
                            </p>
                        </div>
                    </div>
                </div>
                <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center">
                        <div className="text-2xl mr-3">👨‍⚕️</div>
                        <div>
                            <p className="text-sm text-gray-600">Unique Doctors</p>
                            <p className="text-xl font-bold text-gray-900">
                                {new Set(records.map(r => r.doctorId)).size}
                            </p>
                        </div>
                    </div>
                </div>
                <div className="bg-white rounded-lg shadow p-4">
                    <div className="flex items-center">
                        <div className="text-2xl mr-3">🔒</div>
                        <div>
                            <p className="text-sm text-gray-600">Hash Protected</p>
                            <p className="text-xl font-bold text-green-600">100%</p>
                        </div>
                    </div>
                </div>
            </div>

            {/* Add Record Form Modal */}
            {showAddForm && (
                <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
                    <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
                        <div className="p-6">
                            <div className="flex items-center justify-between mb-6">
                                <h3 className="text-xl font-bold text-gray-900">Add New Patient Record</h3>
                                <button
                                    onClick={() => setShowAddForm(false)}
                                    className="text-gray-400 hover:text-gray-600"
                                >
                                    ✕
                                </button>
                            </div>

                            <form onSubmit={handleSubmit} className="space-y-4">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">
                                            Patient Name *
                                        </label>
                                        <input
                                            type="text"
                                            value={formData.patientName}
                                            onChange={(e) => setFormData({ ...formData, patientName: e.target.value })}
                                            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                            placeholder="Enter patient name"
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">
                                            Doctor Name *
                                        </label>
                                        <input
                                            type="text"
                                            value={formData.doctorName}
                                            onChange={(e) => setFormData({ ...formData, doctorName: e.target.value })}
                                            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                            placeholder="Enter doctor name"
                                            required
                                        />
                                    </div>
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Date of Birth
                                    </label>
                                    <input
                                        type="date"
                                        value={formData.dateOfBirth}
                                        onChange={(e) => setFormData({ ...formData, dateOfBirth: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Diagnosis *
                                    </label>
                                    <textarea
                                        value={formData.diagnosis}
                                        onChange={(e) => setFormData({ ...formData, diagnosis: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent h-24"
                                        placeholder="Enter diagnosis details"
                                        required
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Prescription
                                    </label>
                                    <textarea
                                        value={formData.prescription}
                                        onChange={(e) => setFormData({ ...formData, prescription: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent h-24"
                                        placeholder="Enter prescription details"
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Additional Notes
                                    </label>
                                    <textarea
                                        value={formData.notes}
                                        onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent h-24"
                                        placeholder="Enter any additional notes"
                                    />
                                </div>

                                <div className="flex items-center justify-between pt-4">
                                    <p className="text-sm text-gray-600">
                                        * Required fields. IDs will be auto-generated.
                                    </p>
                                    <div className="flex gap-2">
                                        <button
                                            type="button"
                                            onClick={() => setShowAddForm(false)}
                                            className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
                                        >
                                            Cancel
                                        </button>
                                        <button
                                            type="submit"
                                            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                                        >
                                            Add Record
                                        </button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}

            {/* Records List */}
            <div className="bg-white rounded-lg shadow">
                <div className="p-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-4">
                        Medical Records ({filteredRecords.length})
                    </h3>

                    {filteredRecords.length === 0 ? (
                        <div className="text-center py-12">
                            <div className="text-6xl mb-4">📋</div>
                            <h3 className="text-lg font-medium text-gray-900 mb-2">No Records Found</h3>
                            <p className="text-gray-600 mb-4">
                                {records.length === 0
                                    ? "Get started by adding your first patient record."
                                    : "Try adjusting your search criteria."
                                }
                            </p>
                            {records.length === 0 && (
                                <button
                                    onClick={() => setShowAddForm(true)}
                                    className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                                >
                                    Add First Record
                                </button>
                            )}
                        </div>
                    ) : (
                        <div className="overflow-x-auto">
                            <table className="min-w-full divide-y divide-gray-200">
                                <thead className="bg-gray-50">
                                    <tr>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Record Info
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Patient
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Doctor
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Diagnosis
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Hash
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Actions
                                        </th>
                                    </tr>
                                </thead>
                                <tbody className="bg-white divide-y divide-gray-200">
                                    {filteredRecords.map((record) => (
                                        <tr key={record.id} className="hover:bg-gray-50">
                                            <td className="px-6 py-4 whitespace-nowrap">
                                                <div>
                                                    <div className="text-sm font-medium text-gray-900">
                                                        {record.recordId}
                                                    </div>
                                                    <div className="text-sm text-gray-500">
                                                        {new Date(record.createdAt).toLocaleDateString()}
                                                    </div>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap">
                                                <div>
                                                    <div className="text-sm font-medium text-gray-900">
                                                        {record.patientName}
                                                    </div>
                                                    <div className="text-sm text-gray-500">
                                                        {record.patientId}
                                                    </div>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap">
                                                <div>
                                                    <div className="text-sm font-medium text-gray-900">
                                                        {record.doctorName}
                                                    </div>
                                                    <div className="text-sm text-gray-500">
                                                        {record.doctorId}
                                                    </div>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4">
                                                <div className="text-sm text-gray-900 max-w-xs truncate">
                                                    {record.diagnosis}
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap">
                                                <div className="text-xs font-mono text-gray-500 bg-gray-100 p-1 rounded max-w-24 truncate">
                                                    {record.recordHash.substring(0, 8)}...
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                                <button
                                                    onClick={() => exportToPDF(record)}
                                                    className="text-blue-600 hover:text-blue-900 mr-3"
                                                    title="Download PDF"
                                                >
                                                    📄 PDF
                                                </button>
                                                <button
                                                    onClick={() => navigator.clipboard.writeText(record.recordHash)}
                                                    className="text-green-600 hover:text-green-900"
                                                    title="Copy Hash"
                                                >
                                                    📋 Hash
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PatientHealthRecords;
