import React, { useState, useEffect } from 'react';
import Badge from '../UI/Badge';
import Timer from '../UI/Timer';
import { User, MedicalRecord, Patient, InsuranceClaim, NFTHealthRecord } from '../../types';
import { RoleBasedAccessService } from '../../services/rbacService';
import { EncryptionService } from '../../services/encryption';

interface DoctorDashboardProps {
    user: User;
    onLogout: () => void;
}

const DoctorDashboard: React.FC<DoctorDashboardProps> = ({ user, onLogout }) => {
    const [sessionTimeLeft] = useState(1800);
    const [rbacService] = useState(new RoleBasedAccessService());
    const [encryptionService] = useState(new EncryptionService());
    const [activeTab, setActiveTab] = useState<'patients' | 'records' | 'insurance' | 'nfts' | 'security'>('patients');

    // Modal states
    const [showCreateRecordModal, setShowCreateRecordModal] = useState(false);
    const [showClaimModal, setShowClaimModal] = useState(false);
    const [, setSelectedPatientId] = useState<string>('');

    // Security states
    const [clipboardMonitoring, setClipboardMonitoring] = useState(false);
    const [encryptedRecords, setEncryptedRecords] = useState<{ [key: string]: string }>({});

    // Form states
    const [newRecordForm, setNewRecordForm] = useState({
        patientId: '',
        title: '',
        description: '',
        diagnosis: '',
        prescription: '',
        notes: ''
    });

    const [newClaimForm, setNewClaimForm] = useState({
        patientId: '',
        amount: '',
        description: '',
        insuranceProvider: ''
    });

    const [myPatients] = useState<Patient[]>([
        {
            id: 'patient-1',
            name: 'Michael Johnson',
            email: 'mjohnson@email.com',
            dateOfBirth: '1985-03-15',
            phoneNumber: '+1-555-0123',
            address: '123 Main St, City, State 12345',
            emergencyContact: 'Jane Johnson - +1-555-0124',
            insuranceProvider: 'HealthFirst Insurance',
            insurancePolicyNumber: 'HF-12345-MJ',
            lastVisit: '2025-06-15',
            upcomingAppointment: '2025-07-10',
            isActive: true
        },
        {
            id: 'patient-2',
            name: 'Sarah Wilson',
            email: 'swilson@email.com',
            dateOfBirth: '1990-08-22',
            phoneNumber: '+1-555-0125',
            address: '456 Oak Ave, City, State 12345',
            emergencyContact: 'Robert Wilson - +1-555-0126',
            insuranceProvider: 'MediCare Plus',
            insurancePolicyNumber: 'MP-67890-SW',
            lastVisit: '2025-06-20',
            upcomingAppointment: '2025-07-08',
            isActive: true
        }
    ]);

    const [recentRecords] = useState<MedicalRecord[]>([
        {
            id: 'record-1',
            patientId: 'patient-1',
            doctorId: user.id,
            title: 'Annual Physical Examination',
            description: 'Comprehensive health checkup including blood work and vital signs',
            date: '2025-07-04',
            diagnosis: 'Excellent health, all vitals normal',
            prescription: 'Continue healthy lifestyle, return in 6 months',
            isEncrypted: true,
            blockchainHash: '0xabc123def456789abcdef123456789abc',
            isVerified: true,
            accessPermissions: ['patient-1', user.id],
            ipfsHash: 'QmX9ZB7tRvWKHGK8P2mNv3qL5rA9wS6cF4dE2gH1jK0mP',
            nftTokenId: 'HEALTH-NFT-001'
        },
        {
            id: 'record-2',
            patientId: 'patient-2',
            doctorId: user.id,
            title: 'Follow-up Consultation',
            description: 'Review of previous treatment and medication adjustment',
            date: '2025-07-03',
            diagnosis: 'Improvement noted, continue current treatment',
            prescription: 'Adjust medication dosage, schedule follow-up in 3 weeks',
            isEncrypted: true,
            blockchainHash: '0xdef456abc789123def456abc789123def',
            isVerified: true,
            accessPermissions: ['patient-2', user.id],
            ipfsHash: 'QmY8AX6sRvWKHGK8P2mNv3qL5rA9wS6cF4dE2gH1jK0nQ',
            nftTokenId: 'HEALTH-NFT-002'
        }
    ]);

    const [insuranceClaims] = useState<InsuranceClaim[]>([
        {
            id: 'claim-1',
            claimNumber: 'CLM-2025-001',
            patientId: 'patient-1',
            providerId: user.id,
            doctorId: user.id,
            insuranceCompany: 'HealthFirst Insurance',
            insuranceProvider: 'HealthFirst Insurance',
            amount: 450.00,
            status: 'approved',
            submittedAt: '2025-07-01T10:00:00Z',
            submissionDate: '2025-07-01',
            approvalDate: '2025-07-03',
            description: 'Annual physical examination and blood work',
            relatedRecords: ['record-1'],
            smartContractAddress: '0x1234567890abcdef1234567890abcdef12345678',
            blockchainTxId: '0xabc123def456789abcdef123456789abc123def456789abcdef',
            isProcessedOnChain: true,
            approvalSignatures: [],
            ipfsDocuments: []
        },
        {
            id: 'claim-2',
            claimNumber: 'CLM-2025-002',
            patientId: 'patient-2',
            providerId: user.id,
            doctorId: user.id,
            insuranceCompany: 'MediCare Plus',
            insuranceProvider: 'MediCare Plus',
            amount: 275.50,
            status: 'pending',
            submittedAt: '2025-07-04T14:30:00Z',
            submissionDate: '2025-07-04',
            description: 'Follow-up consultation and medication review',
            relatedRecords: ['record-2'],
            smartContractAddress: '0x1234567890abcdef1234567890abcdef12345678',
            blockchainTxId: '0xdef456abc789123def456abc789123def456abc789123def',
            isProcessedOnChain: false,
            approvalSignatures: [],
            ipfsDocuments: []
        }
    ]);

    const [nftRecords] = useState<NFTHealthRecord[]>([
        {
            id: 'nft-1',
            tokenId: 'HEALTH-NFT-001',
            patientId: 'patient-1',
            recordId: 'record-1',
            name: 'Annual Physical 2025',
            description: 'Comprehensive health examination record',
            imageUrl: 'https://via.placeholder.com/200x200/4F46E5/FFFFFF?text=NFT-001',
            metadataUri: 'ipfs://QmX9ZB7tRvWKHGK8P2mNv3qL5rA9wS6cF4dE2gH1jK0mP',
            contractAddress: '0x9876543210fedcba9876543210fedcba98765432',
            blockchainNetwork: 'Ethereum',
            mintDate: '2025-07-04T10:30:00Z',
            currentOwner: 'patient-1',
            isTransferable: false,
            accessLevel: 'doctor_patient'
        },
        {
            id: 'nft-2',
            tokenId: 'HEALTH-NFT-002',
            patientId: 'patient-2',
            recordId: 'record-2',
            name: 'Follow-up Consultation',
            description: 'Treatment follow-up and medication adjustment',
            imageUrl: 'https://via.placeholder.com/200x200/059669/FFFFFF?text=NFT-002',
            metadataUri: 'ipfs://QmY8AX6sRvWKHGK8P2mNv3qL5rA9wS6cF4dE2gH1jK0nQ',
            contractAddress: '0x9876543210fedcba9876543210fedcba98765432',
            blockchainNetwork: 'Ethereum',
            mintDate: '2025-07-03T14:45:00Z',
            currentOwner: 'patient-2',
            isTransferable: false,
            accessLevel: 'doctor_patient'
        }
    ]);

    useEffect(() => {
        rbacService.setCurrentUser(user);
    }, [user, rbacService]);

    const handleSessionExpire = () => {
        window.confirm('Session expired for security. Please login again.') && onLogout();
    };

    const handleCreateRecord = () => {
        setShowCreateRecordModal(true);
    };

    const handleSubmitInsuranceClaim = (patientId: string) => {
        setSelectedPatientId(patientId);
        setNewClaimForm(prev => ({ ...prev, patientId }));
        setShowClaimModal(true);
    };

    const handleMintNFT = (recordId: string) => {
        alert(`NFT minting initiated for record ${recordId}. This will create a secure, immutable NFT on the blockchain.`);
        console.log(`Minting NFT for record: ${recordId}`);
    };

    const handleViewHistory = (patientId: string) => {
        const patient = myPatients.find(p => p.id === patientId);
        const patientRecords = recentRecords.filter(record => record.patientId === patientId);

        if (patient && patientRecords.length > 0) {
            const historyDetails = patientRecords.map(record =>
                `📋 ${record.title} (${record.date})\n   Diagnosis: ${record.diagnosis}\n   Prescription: ${record.prescription}`
            ).join('\n\n');

            alert(`👥 Medical History for ${patient.name}
📧 Email: ${patient.email}
🎂 DOB: ${patient.dateOfBirth}
🏥 Insurance: ${patient.insuranceProvider}
📞 Phone: ${patient.phoneNumber}
🏠 Address: ${patient.address}
🚨 Emergency Contact: ${patient.emergencyContact}
📅 Last Visit: ${patient.lastVisit}
⏭️ Next Appointment: ${patient.upcomingAppointment}

📋 MEDICAL RECORDS:
${historyDetails}

Total Records: ${patientRecords.length}`);
        } else {
            alert(`No medical history found for this patient.`);
        }
    };

    const handleEncryptRecord = (recordId: string) => {
        const record = recentRecords.find(r => r.id === recordId);
        if (record) {
            try {
                const encryptedData = encryptionService.encryptData(JSON.stringify(record));
                setEncryptedRecords(prev => ({ ...prev, [recordId]: encryptedData }));
                alert(`🔒 Record "${record.title}" has been successfully encrypted and secured.`);
            } catch (error) {
                alert('❌ Encryption failed. Please try again.');
            }
        }
    };

    const handleDecryptRecord = (recordId: string) => {
        const encryptedData = encryptedRecords[recordId];
        if (encryptedData) {
            try {
                const decryptedData = encryptionService.decryptData(encryptedData);
                const record = JSON.parse(decryptedData);
                alert(`🔓 Decrypted Record Details:
                
📋 Title: ${record.title}
👤 Patient: ${record.patientId}
📅 Date: ${record.date}
🩺 Diagnosis: ${record.diagnosis}
💊 Prescription: ${record.prescription}
📝 Description: ${record.description}`);
            } catch (error) {
                alert('❌ Decryption failed. Invalid data or corrupted encryption.');
            }
        } else {
            alert('❌ No encrypted data found for this record.');
        }
    };

    const toggleClipboardMonitoring = () => {
        const newState = !clipboardMonitoring;
        setClipboardMonitoring(newState);
        if (newState) {
            alert('🔍 Clipboard monitoring enabled. The system will monitor for suspicious wallet addresses.');
            // In a real application, you would start actual clipboard monitoring here
        } else {
            alert('⏹️ Clipboard monitoring disabled.');
        }
    };

    const handleViewInsuranceClaim = (claimId: string) => {
        const claim = insuranceClaims.find(c => c.id === claimId);
        if (claim) {
            const patient = myPatients.find(p => p.id === claim.patientId);
            alert(`💰 Insurance Claim Details

📋 Claim Number: ${claim.claimNumber}
👤 Patient: ${patient?.name || claim.patientId}
💵 Amount: $${claim.amount.toFixed(2)}
📊 Status: ${claim.status.toUpperCase()}
🏥 Insurance Provider: ${claim.insuranceProvider}
📅 Submitted: ${claim.submissionDate}
${claim.approvalDate ? `✅ Approved: ${claim.approvalDate}` : '⏳ Pending approval'}
📝 Description: ${claim.description || 'No description provided'}

🔗 Blockchain Details:
📋 Contract Address: ${claim.smartContractAddress}
🆔 Transaction ID: ${claim.blockchainTxId}
⛓️ On-Chain Status: ${claim.isProcessedOnChain ? 'Processed' : 'Pending'}

📄 Related Records: ${claim.relatedRecords.length} record(s)
📎 Documents: ${claim.ipfsDocuments.length} file(s) on IPFS`);
        }
    };

    const handleSaveRecord = () => {
        if (!newRecordForm.title || !newRecordForm.patientId) {
            alert('Please fill in all required fields');
            return;
        }

        // Create new record
        // const newRecord: MedicalRecord = {
        //     id: `record-${Date.now()}`,
        //     patientId: newRecordForm.patientId,
        //     doctorId: user.id,
        //     title: newRecordForm.title,
        //     description: newRecordForm.description,
        //     date: new Date().toISOString(),
        //     diagnosis: newRecordForm.diagnosis,
        //     prescription: newRecordForm.prescription,
        //     isEncrypted: true,
        //     isVerified: true,
        //     accessPermissions: [user.id, newRecordForm.patientId],
        //     nftTokenId: `NFT-${Date.now()}`
        // };

        alert('Medical record created successfully!');
        setShowCreateRecordModal(false);
        setNewRecordForm({
            patientId: '',
            title: '',
            description: '',
            diagnosis: '',
            prescription: '',
            notes: ''
        });
    };

    const handleSaveClaim = () => {
        if (!newClaimForm.amount || !newClaimForm.description) {
            alert('Please fill in all required fields');
            return;
        }

        // Create new insurance claim
        // const newClaim: InsuranceClaim = {
        //     id: `claim-${Date.now()}`,
        //     claimNumber: `CLAIM-${Date.now()}`,
        //     patientId: newClaimForm.patientId,
        //     providerId: user.id,
        //     doctorId: user.id,
        //     insuranceCompany: newClaimForm.insuranceProvider,
        //     insuranceProvider: newClaimForm.insuranceProvider,
        //     amount: parseFloat(newClaimForm.amount),
        //     status: 'pending',
        //     submittedAt: new Date().toISOString(),
        //     submissionDate: new Date().toLocaleDateString(),
        //     description: newClaimForm.description,
        //     relatedRecords: [],
        //     smartContractAddress: '0x1234567890abcdef1234567890abcdef12345678',
        //     blockchainTxId: `0x${Date.now().toString(16)}`,
        //     isProcessedOnChain: false,
        //     approvalSignatures: [],
        //     ipfsDocuments: []
        // };

        alert('Insurance claim submitted successfully!');
        setShowClaimModal(false);
        setNewClaimForm({
            patientId: '',
            amount: '',
            description: '',
            insuranceProvider: ''
        });
    };

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'approved': return 'success';
            case 'pending': return 'warning';
            case 'rejected': return 'error';
            case 'paid': return 'success';
            case 'under_review': return 'info';
            case 'submitted': return 'primary';
            default: return 'secondary';
        }
    };

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Header */}
            <header className="bg-white shadow-sm border-b">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center h-16">
                        <div className="flex items-center">
                            <div className="flex-shrink-0">
                                <h1 className="text-xl font-bold text-gray-900">🩺 Doctor Dashboard</h1>
                            </div>
                            <div className="ml-4">
                                <Badge variant="success" size="sm">🔒 HIPAA Compliant</Badge>
                                <Badge variant="primary" size="sm" className="ml-2">👨‍⚕️ Medical Access</Badge>
                            </div>
                        </div>

                        <div className="flex items-center space-x-4">
                            <Timer
                                duration={sessionTimeLeft}
                                onExpire={handleSessionExpire}
                                className="text-sm"
                            />

                            <div className="flex items-center space-x-2">
                                <img
                                    className="h-8 w-8 rounded-full"
                                    src={`https://ui-avatars.com/api/?name=${user.name}&background=2563eb&color=fff`}
                                    alt={user.name}
                                />
                                <div className="text-sm">
                                    <div className="font-medium text-gray-900">{user.name}</div>
                                    <div className="text-blue-600 font-semibold">{user.role.toUpperCase()}</div>
                                </div>
                            </div>

                            <button
                                onClick={onLogout}
                                className="bg-red-600 text-white px-3 py-1 rounded text-sm hover:bg-red-700"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </header>

            {/* Navigation Tabs */}
            <div className="bg-white border-b">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <nav className="flex space-x-8">
                        {[
                            { id: 'patients', label: '👥 My Patients', icon: '👥' },
                            { id: 'records', label: '📋 Medical Records', icon: '📋' },
                            { id: 'insurance', label: '💰 Insurance Claims', icon: '💰' },
                            { id: 'nfts', label: '🎨 NFT Health Records', icon: '🎨' },
                            { id: 'security', label: '🔒 Security Tools', icon: '🔒' }
                        ].map((tab) => (
                            <button
                                key={tab.id}
                                onClick={() => setActiveTab(tab.id as any)}
                                className={`py-4 px-1 border-b-2 font-medium text-sm ${activeTab === tab.id
                                    ? 'border-blue-500 text-blue-600'
                                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                                    }`}
                            >
                                {tab.label}
                            </button>
                        ))}
                    </nav>
                </div>
            </div>

            {/* Main Content */}
            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">

                {/* Patients Tab */}
                {activeTab === 'patients' && (
                    <div className="bg-white shadow rounded-lg">
                        <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
                            <h3 className="text-lg font-medium text-gray-900">👥 My Patients</h3>
                            <div className="text-sm text-gray-500">
                                {myPatients.length} active patients
                            </div>
                        </div>
                        <div className="p-6">
                            <div className="grid gap-6">
                                {myPatients.map((patient) => (
                                    <div key={patient.id} className="border rounded-lg p-6 hover:shadow-md transition-shadow">
                                        <div className="flex items-start justify-between">
                                            <div className="flex items-center space-x-4">
                                                <img
                                                    className="h-12 w-12 rounded-full"
                                                    src={`https://ui-avatars.com/api/?name=${patient.name}&background=random`}
                                                    alt={patient.name}
                                                />
                                                <div>
                                                    <h4 className="text-lg font-medium text-gray-900">{patient.name}</h4>
                                                    <p className="text-sm text-gray-500">{patient.email}</p>
                                                    <p className="text-sm text-gray-500">DOB: {patient.dateOfBirth}</p>
                                                    <p className="text-sm text-gray-500">Phone: {patient.phoneNumber}</p>
                                                </div>
                                            </div>
                                            <div className="text-right">
                                                <Badge variant="success" size="sm">Active</Badge>
                                                <p className="text-sm text-gray-500 mt-2">Last Visit: {patient.lastVisit}</p>
                                                <p className="text-sm text-blue-600 font-medium">Next: {patient.upcomingAppointment}</p>
                                            </div>
                                        </div>
                                        <div className="mt-4 grid grid-cols-1 md:grid-cols-2 gap-4">
                                            <div>
                                                <p className="text-sm font-medium text-gray-900">Insurance</p>
                                                <p className="text-sm text-gray-500">{patient.insuranceProvider}</p>
                                                <p className="text-sm text-gray-400 font-mono">{patient.insurancePolicyNumber}</p>
                                            </div>
                                            <div>
                                                <p className="text-sm font-medium text-gray-900">Emergency Contact</p>
                                                <p className="text-sm text-gray-500">{patient.emergencyContact}</p>
                                            </div>
                                        </div>
                                        <div className="mt-4 flex space-x-3">
                                            <button
                                                onClick={handleCreateRecord}
                                                className="bg-blue-600 text-white px-4 py-2 rounded text-sm hover:bg-blue-700"
                                            >
                                                📋 New Record
                                            </button>
                                            <button
                                                onClick={() => handleSubmitInsuranceClaim(patient.id)}
                                                className="bg-green-600 text-white px-4 py-2 rounded text-sm hover:bg-green-700"
                                            >
                                                💰 Submit Claim
                                            </button>
                                            <button
                                                onClick={() => handleViewHistory(patient.id)}
                                                className="bg-gray-600 text-white px-4 py-2 rounded text-sm hover:bg-gray-700"
                                            >
                                                👁️ View History
                                            </button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}

                {/* Medical Records Tab */}
                {activeTab === 'records' && (
                    <div className="bg-white shadow rounded-lg">
                        <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
                            <h3 className="text-lg font-medium text-gray-900">📋 Recent Medical Records</h3>
                            <button
                                onClick={handleCreateRecord}
                                className="bg-blue-600 text-white px-4 py-2 rounded text-sm hover:bg-blue-700"
                            >
                                + New Record
                            </button>
                        </div>
                        <div className="p-6">
                            <div className="space-y-6">
                                {recentRecords.map((record) => (
                                    <div key={record.id} className="border rounded-lg p-6">
                                        <div className="flex items-start justify-between">
                                            <div className="flex-1">
                                                <div className="flex items-center space-x-2 mb-2">
                                                    <h4 className="text-lg font-medium text-gray-900">{record.title}</h4>
                                                    <Badge variant="success" size="sm">🔒 Encrypted</Badge>
                                                    <Badge variant="primary" size="sm">🧱 On-Chain</Badge>
                                                    {record.nftTokenId && (
                                                        <Badge variant="secondary" size="sm">🎨 NFT</Badge>
                                                    )}
                                                </div>
                                                <p className="text-sm text-gray-600 mb-2">{record.description}</p>
                                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                                                    <div>
                                                        <p><span className="font-medium">Patient:</span> {myPatients.find(p => p.id === record.patientId)?.name}</p>
                                                        <p><span className="font-medium">Date:</span> {record.date}</p>
                                                        <p><span className="font-medium">Diagnosis:</span> {record.diagnosis}</p>
                                                    </div>
                                                    <div>
                                                        <p><span className="font-medium">Prescription:</span> {record.prescription}</p>
                                                        <p className="font-mono text-xs"><span className="font-medium">Blockchain:</span> {record.blockchainHash}</p>
                                                        {record.ipfsHash && (
                                                            <p className="font-mono text-xs"><span className="font-medium">IPFS:</span> {record.ipfsHash}</p>
                                                        )}
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="ml-4 flex flex-col space-y-2">
                                                {!record.nftTokenId && (
                                                    <button
                                                        onClick={() => handleMintNFT(record.id)}
                                                        className="bg-purple-600 text-white px-3 py-1 rounded text-sm hover:bg-purple-700"
                                                    >
                                                        🎨 Mint NFT
                                                    </button>
                                                )}
                                                <button
                                                    onClick={() => handleEncryptRecord(record.id)}
                                                    className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700"
                                                >
                                                    🔒 Encrypt
                                                </button>
                                                <button
                                                    onClick={() => handleDecryptRecord(record.id)}
                                                    className="bg-orange-600 text-white px-3 py-1 rounded text-sm hover:bg-orange-700"
                                                >
                                                    🔓 Decrypt
                                                </button>
                                                <button className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700">
                                                    👁️ View
                                                </button>
                                                <button className="bg-gray-600 text-white px-3 py-1 rounded text-sm hover:bg-gray-700">
                                                    ✏️ Edit
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}

                {/* Insurance Claims Tab */}
                {activeTab === 'insurance' && (
                    <div className="bg-white shadow rounded-lg">
                        <div className="px-6 py-4 border-b border-gray-200">
                            <h3 className="text-lg font-medium text-gray-900">💰 Insurance Claims</h3>
                        </div>
                        <div className="p-6">
                            <div className="space-y-6">
                                {insuranceClaims.map((claim) => (
                                    <div key={claim.id} className="border rounded-lg p-6">
                                        <div className="flex items-start justify-between">
                                            <div className="flex-1">
                                                <div className="flex items-center space-x-2 mb-2">
                                                    <h4 className="text-lg font-medium text-gray-900">Claim #{claim.claimNumber}</h4>
                                                    <Badge variant={getStatusColor(claim.status)} size="sm">
                                                        {claim.status.toUpperCase()}
                                                    </Badge>
                                                    {claim.isProcessedOnChain && (
                                                        <Badge variant="primary" size="sm">🧱 On-Chain</Badge>
                                                    )}
                                                </div>
                                                <p className="text-sm text-gray-600 mb-2">{claim.description}</p>
                                                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                                                    <div>
                                                        <p><span className="font-medium">Patient:</span> {myPatients.find(p => p.id === claim.patientId)?.name}</p>
                                                        <p><span className="font-medium">Amount:</span> ${claim.amount.toFixed(2)}</p>
                                                        <p><span className="font-medium">Submitted:</span> {claim.submissionDate}</p>
                                                    </div>
                                                    <div>
                                                        <p><span className="font-medium">Provider:</span> {claim.insuranceProvider}</p>
                                                        {claim.approvalDate && (
                                                            <p><span className="font-medium">Approved:</span> {claim.approvalDate}</p>
                                                        )}
                                                        <p className="font-mono text-xs"><span className="font-medium">Contract:</span> {claim.smartContractAddress}</p>
                                                    </div>
                                                    <div>
                                                        <p className="font-mono text-xs"><span className="font-medium">Tx ID:</span> {claim.blockchainTxId?.substring(0, 20)}...</p>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="ml-4">
                                                <button
                                                    onClick={() => handleViewInsuranceClaim(claim.id)}
                                                    className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700"
                                                >
                                                    👁️ View Details
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}

                {/* NFT Health Records Tab */}
                {activeTab === 'nfts' && (
                    <div className="bg-white shadow rounded-lg">
                        <div className="px-6 py-4 border-b border-gray-200">
                            <h3 className="text-lg font-medium text-gray-900">🎨 NFT Health Records</h3>
                        </div>
                        <div className="p-6">
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                                {nftRecords.map((nft) => (
                                    <div key={nft.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                                        <div className="aspect-square bg-gradient-to-br from-blue-400 to-purple-500 rounded-lg mb-4 flex items-center justify-center">
                                            <span className="text-white text-4xl">🏥</span>
                                        </div>
                                        <div className="space-y-2">
                                            <h4 className="font-medium text-gray-900">{nft.name}</h4>
                                            <p className="text-sm text-gray-600">{nft.description}</p>
                                            <div className="text-xs space-y-1">
                                                <p><span className="font-medium">Token ID:</span> {nft.tokenId}</p>
                                                <p><span className="font-medium">Patient:</span> {myPatients.find(p => p.id === nft.patientId)?.name}</p>
                                                <p><span className="font-medium">Network:</span> {nft.blockchainNetwork}</p>
                                                <p><span className="font-medium">Minted:</span> {new Date(nft.mintDate).toLocaleDateString()}</p>
                                                <p className="font-mono"><span className="font-medium">Contract:</span> {nft.contractAddress.substring(0, 10)}...</p>
                                            </div>
                                            <div className="flex items-center justify-between pt-2">
                                                <Badge variant="secondary" size="sm">
                                                    {nft.isTransferable ? 'Transferable' : 'Non-Transferable'}
                                                </Badge>
                                                <button className="bg-purple-600 text-white px-2 py-1 rounded text-xs hover:bg-purple-700">
                                                    👁️ View NFT
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}

                {/* Security Tools Tab */}
                {activeTab === 'security' && (
                    <div className="space-y-6">
                        <div className="bg-white shadow rounded-lg">
                            <div className="px-6 py-4 border-b border-gray-200">
                                <h3 className="text-lg font-medium text-gray-900">🔒 Encryption & Security Tools</h3>
                                <p className="text-sm text-gray-500 mt-1">Secure patient records with advanced encryption</p>
                            </div>
                            <div className="p-6">
                                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                                    <div className="space-y-4">
                                        <h4 className="font-medium text-gray-900">🔐 Record Encryption</h4>
                                        <p className="text-sm text-gray-600">Encrypt sensitive medical records for enhanced security</p>
                                        <div className="space-y-2">
                                            {recentRecords.slice(0, 3).map((record) => (
                                                <div key={record.id} className="flex items-center justify-between p-3 border rounded">
                                                    <div>
                                                        <p className="font-medium text-sm">{record.title}</p>
                                                        <p className="text-xs text-gray-500">{record.patientId}</p>
                                                    </div>
                                                    <div className="flex space-x-2">
                                                        <button
                                                            onClick={() => handleEncryptRecord(record.id)}
                                                            className="bg-green-600 text-white px-2 py-1 rounded text-xs hover:bg-green-700"
                                                        >
                                                            🔒 Encrypt
                                                        </button>
                                                        <button
                                                            onClick={() => handleDecryptRecord(record.id)}
                                                            className="bg-orange-600 text-white px-2 py-1 rounded text-xs hover:bg-orange-700"
                                                        >
                                                            🔓 Decrypt
                                                        </button>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    </div>

                                    <div className="space-y-4">
                                        <h4 className="font-medium text-gray-900">📋 Clipboard Security</h4>
                                        <p className="text-sm text-gray-600">Monitor clipboard for suspicious wallet addresses</p>
                                        <div className="p-4 border rounded-lg">
                                            <div className="flex items-center justify-between mb-3">
                                                <span className="text-sm font-medium">Clipboard Monitoring</span>
                                                <Badge variant={clipboardMonitoring ? 'success' : 'secondary'} size="sm">
                                                    {clipboardMonitoring ? 'Active' : 'Inactive'}
                                                </Badge>
                                            </div>
                                            <button
                                                onClick={toggleClipboardMonitoring}
                                                className={`w-full px-4 py-2 rounded text-sm ${clipboardMonitoring
                                                        ? 'bg-red-600 hover:bg-red-700 text-white'
                                                        : 'bg-blue-600 hover:bg-blue-700 text-white'
                                                    }`}
                                            >
                                                {clipboardMonitoring ? '⏹️ Stop Monitoring' : '🔍 Start Monitoring'}
                                            </button>
                                            <div className="mt-3 text-xs text-gray-500">
                                                <p>• Detects cryptocurrency wallet addresses</p>
                                                <p>• Alerts on suspicious clipboard activity</p>
                                                <p>• Protects against clipboard hijacking</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
                                    <div className="flex items-start">
                                        <span className="text-yellow-600 text-lg mr-2">⚠️</span>
                                        <div>
                                            <h5 className="font-medium text-yellow-800">Security Notice</h5>
                                            <p className="text-sm text-yellow-700 mt-1">
                                                Always ensure patient records are encrypted before transmission.
                                                Use blockchain verification for data integrity and monitor for suspicious activities.
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </main>

            {/* Create Record Modal */}
            {showCreateRecordModal && (
                <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
                    <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
                        <div className="mt-3">
                            <h3 className="text-lg font-medium text-gray-900 mb-4">Create New Medical Record</h3>
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Patient</label>
                                    <select
                                        value={newRecordForm.patientId}
                                        onChange={(e) => setNewRecordForm(prev => ({ ...prev, patientId: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                    >
                                        <option value="">Select Patient</option>
                                        {myPatients.map((patient) => (
                                            <option key={patient.id} value={patient.id}>{patient.name}</option>
                                        ))}
                                    </select>
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Title</label>
                                    <input
                                        type="text"
                                        value={newRecordForm.title}
                                        onChange={(e) => setNewRecordForm(prev => ({ ...prev, title: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                        placeholder="Record title"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Diagnosis</label>
                                    <input
                                        type="text"
                                        value={newRecordForm.diagnosis}
                                        onChange={(e) => setNewRecordForm(prev => ({ ...prev, diagnosis: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                        placeholder="Diagnosis"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Prescription</label>
                                    <textarea
                                        value={newRecordForm.prescription}
                                        onChange={(e) => setNewRecordForm(prev => ({ ...prev, prescription: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                        rows={3}
                                        placeholder="Prescription details"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Description</label>
                                    <textarea
                                        value={newRecordForm.description}
                                        onChange={(e) => setNewRecordForm(prev => ({ ...prev, description: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                        rows={3}
                                        placeholder="Additional notes"
                                    />
                                </div>
                            </div>
                            <div className="flex justify-end space-x-3 mt-6">
                                <button
                                    onClick={() => setShowCreateRecordModal(false)}
                                    className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400"
                                >
                                    Cancel
                                </button>
                                <button
                                    onClick={handleSaveRecord}
                                    className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                                >
                                    Create Record
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Insurance Claim Modal */}
            {showClaimModal && (
                <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
                    <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
                        <div className="mt-3">
                            <h3 className="text-lg font-medium text-gray-900 mb-4">Submit Insurance Claim</h3>
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Patient</label>
                                    <select
                                        value={newClaimForm.patientId}
                                        onChange={(e) => setNewClaimForm(prev => ({ ...prev, patientId: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                    >
                                        <option value="">Select Patient</option>
                                        {myPatients.map((patient) => (
                                            <option key={patient.id} value={patient.id}>{patient.name}</option>
                                        ))}
                                    </select>
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Insurance Provider</label>
                                    <input
                                        type="text"
                                        value={newClaimForm.insuranceProvider}
                                        onChange={(e) => setNewClaimForm(prev => ({ ...prev, insuranceProvider: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                        placeholder="Insurance company name"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Claim Amount ($)</label>
                                    <input
                                        type="number"
                                        value={newClaimForm.amount}
                                        onChange={(e) => setNewClaimForm(prev => ({ ...prev, amount: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                        placeholder="0.00"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Description</label>
                                    <textarea
                                        value={newClaimForm.description}
                                        onChange={(e) => setNewClaimForm(prev => ({ ...prev, description: e.target.value }))}
                                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                        rows={4}
                                        placeholder="Claim description and details"
                                    />
                                </div>
                            </div>
                            <div className="flex justify-end space-x-3 mt-6">
                                <button
                                    onClick={() => setShowClaimModal(false)}
                                    className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400"
                                >
                                    Cancel
                                </button>
                                <button
                                    onClick={handleSaveClaim}
                                    className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700"
                                >
                                    Submit Claim
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default DoctorDashboard;
